import React from 'react';

interface ScoreEntry {
  name: string;
  score: number;
}

interface LeaderboardProps {
  leaderboard: ScoreEntry[];
}

export default function Leaderboard({ leaderboard }: LeaderboardProps) {
  return (
    <div style={{
      minWidth: "300px",
      background: "linear-gradient(135deg, #1a1a2e, #16213e)",
      padding: "20px",
      borderRadius: "15px",
      border: "2px solid #f3ce13",
      boxShadow: "0 8px 25px rgba(0,0,0,0.4)",
      marginLeft: "25px"
    }}>
      <div style={{
        fontSize: "24px",
        color: "#f3ce13",
        marginBottom: "20px",
        textAlign: "center",
        textShadow: "2px 2px 4px rgba(0,0,0,0.5)",
        fontWeight: "bold"
      }}>
        üèÜ Leaderboard
      </div>
      {leaderboard.length === 0 ? (
        <div style={{
          color: "#fff",
          textAlign: "center",
          fontStyle: "italic",
          padding: "30px 0",
          fontSize: "16px"
        }}>
          No scores yet
        </div>
      ) : (
        <div style={{ maxHeight: "400px", overflowY: "auto" }}>
          {leaderboard.slice(0, 10).map((entry, i) => (
            <div key={i} style={{
              display: "flex", justifyContent: "space-between", alignItems: "center",
              padding: "12px 16px", margin: "8px 0", borderRadius: "8px",
              background: i < 3 ? "linear-gradient(45deg, rgba(243, 206, 19, 0.2), rgba(255, 215, 0, 0.1))" : "rgba(255,255,255,0.1)",
              border: i < 3 ? "2px solid rgba(243, 206, 19, 0.5)" : "1px solid rgba(255,255,255,0.2)",
              transition: "all 0.3s ease"
            }}>
              <div style={{
                display: "flex", alignItems: "center", gap: "10px"
              }}>
                <span style={{
                  fontSize: "18px", fontWeight: "bold",
                  color: i === 0 ? "#f3ce13" : i === 1 ? "#C0C0C0" : i === 2 ? "#CD7F32" : "#fff"
                }}>
                  {i === 0 ? "ü•á" : i === 1 ? "ü•à" : i === 2 ? "ü•â" : `${i + 1}.`}
                </span>
                <span style={{
                  color: i === 0 ? "#f3ce13" : i === 1 ? "#C0C0C0" : i === 2 ? "#CD7F32" : "#fff",
                  fontWeight: i < 3 ? "bold" : "normal",
                  fontSize: i < 3 ? "16px" : "14px"
                }}>
                  {entry.name}
                </span>
              </div>
              <span style={{
                color: "#f3ce13",
                fontWeight: "bold",
                fontSize: i < 3 ? "16px" : "14px"
              }}>
                {entry.score.toLocaleString()}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
