export default function Leaderboard({ leaderboard }) {
    return (
      <div style={{
        minWidth: 220, background: "rgba(255,255,255,0.07)", padding: "22px 18px", borderRadius: "16px",
        boxShadow: "0 3px 7px rgba(0,0,0,0.15)", fontSize: "16px"
      }}>
        <div style={{ fontSize: "20px", color: "#f3ce13", marginBottom: "12px" }}>üèÜ Leaderboard</div>
        {leaderboard.length === 0 ? (
          <div style={{ color: "#fff" }}>No scores yet</div>
        ) : (
          <ol style={{ textAlign: "left", marginLeft: "18px" }}>
            {leaderboard.slice(0, 10).map((entry, i) => (
              <li key={i} style={{ marginBottom: "7px", color: "#fff", fontWeight: i === 0 ? "bold" : "normal" }}>
                <span>{i + 1}. {entry.name}</span>
                <span style={{ float: "right" }}>{entry.score}</span>
              </li>
            ))}
          </ol>
        )}
      </div>
    );
  }
