import React, { useRef, useEffect, useState } from "react";
import { renderHarry, renderObstacle, renderEnemy, renderBeams } from "./GameEntities";

// Game configuration constants
const CONFIG = {
  GAME_WIDTH: 400,           // Width of the game canvas
  GAME_HEIGHT: 600,          // Height of the game canvas
  PLAYER_W: 48,              // Harry's width
  PLAYER_H: 48,              // Harry's height
  GRAVITY: 0.6,              // Gravity force pulling Harry down
  FLAP_VEL: -8,              // Velocity when Harry flaps (negative = up)
  OBSTACLE_W: 60,            // Width of obstacles
  OBSTACLE_GAP: 220,         // Gap between top and bottom obstacles (increased for easier passage)
  ENEMY_W: 40,               // Enemy width
  ENEMY_H: 40,               // Enemy height
  BEAM_W: 10,                // Magic beam width
  API: "http://localhost:8000", // Backend API URL
  ENEMY_COLORS: ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"], // Random enemy colors
  ENEMY_NAMES: ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"] // Random enemy types
};

type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

function getRandomObstacle(id: number): Obstacle {
  // Make obstacles much smaller for easier passage
  const safeMin = 10;
  const safeMax = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - 10;
  const height = Math.floor(Math.random() * (safeMax - safeMin) + safeMin);

  // Ensure gap is always very large for easy passage
  const minGapSize = CONFIG.PLAYER_H + 60; // Even larger buffer for very easy escape

  // Calculate actual gap size
  const actualGapSize = CONFIG.OBSTACLE_GAP;

  if (actualGapSize < minGapSize) {
    // Adjust height to ensure minimum gap - make it very generous
    const adjustedHeight = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - minGapSize;
    return { x: CONFIG.GAME_WIDTH, height: Math.max(safeMin, adjustedHeight), id };
  }

  return { x: CONFIG.GAME_WIDTH, height, id };
}

function getRandomEnemy(id: number): Enemy {
  const color = CONFIG.ENEMY_COLORS[Math.floor(Math.random() * CONFIG.ENEMY_COLORS.length)];
  const type = CONFIG.ENEMY_NAMES[Math.floor(Math.random() * CONFIG.ENEMY_NAMES.length)];
  const y = Math.floor(Math.random() * (CONFIG.GAME_HEIGHT - CONFIG.ENEMY_H - 20)) + 10;
  return { x: CONFIG.GAME_WIDTH, y, color, type, id };
}

export default function App() {
  const [playerY, setPlayerY] = useState(CONFIG.GAME_HEIGHT / 2);
  const [vel, setVel] = useState(0);
  const [obstacles, setObstacles] = useState([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
  const [score, setScore] = useState(0);
  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
  const obstacleId = useRef(1);
  const enemyId = useRef(1);

  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      setVel(v => Math.min(v + CONFIG.GRAVITY, 10));
      setPlayerY(y => Math.max(0, Math.min(CONFIG.GAME_HEIGHT - CONFIG.PLAYER_H, y + vel)));
      setScore(s => s + 1);
       setObstacles(obs => {
         let nobs = obs.map(o => ({ ...o, x: o.x - 3 })); // Slower obstacle movement (was 4, now 3)
         if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < CONFIG.GAME_WIDTH - 200)) {
           nobs.push(getRandomObstacle(obstacleId.current++));
         }
         return nobs.filter(o => o.x + CONFIG.OBSTACLE_W > 0);
       });
       setEnemies(enms => {
         let nenms = enms.map(e => ({ ...e, x: e.x - 5 }));
         if (Math.random() > 0.985) { // Reduced spawn rate from 0.97 to 0.985
           nenms.push(getRandomEnemy(enemyId.current++));
         }
         return nenms.filter(e => e.x + CONFIG.ENEMY_W > 0);
       });
      setBeams(bs => bs.filter(b => b.active && b.x < CONFIG.GAME_WIDTH).map(b => ({ ...b, x: b.x + 16 }))); // Faster beam speed
    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  useEffect(() => {
    if (!running) return;
    const flap = () => setVel(CONFIG.FLAP_VEL);
    const cast = () => setBeams(bs => [...bs, { x: 30 + CONFIG.PLAYER_W, y: playerY + CONFIG.PLAYER_H / 2 - 4, active: true }]);
    const onKey = (e: KeyboardEvent) => {
      // Prevent page scrolling for arrow keys when game is running
      if (e.key === "ArrowLeft" || e.key === "ArrowRight" || e.key === "ArrowUp" || e.key === "ArrowDown") {
        e.preventDefault();
      }

      if (e.code === "Space") { e.preventDefault(); flap(); }
      if (e.key === "a" || e.key === "A") cast();
      if (e.key === "ArrowUp") setVel(CONFIG.FLAP_VEL);
    };
    window.addEventListener("keydown", onKey);
    window.addEventListener("mousedown", flap);
    window.addEventListener("touchstart", flap);
    return () => {
      window.removeEventListener("keydown", onKey);
      window.removeEventListener("mousedown", flap);
      window.removeEventListener("touchstart", flap);
    };
  }, [playerY, running]);

  useEffect(() => {
    if (!running) return;
    const harryTop = playerY, harryBot = playerY + CONFIG.PLAYER_H, harryLeft = 30, harryRight = 30 + CONFIG.PLAYER_W;
     for (const obs of obstacles) {
       // More forgiving obstacle collision - add small buffer
       if (obs.x < harryRight + 3 && obs.x + CONFIG.OBSTACLE_W > harryLeft - 3 && (harryTop < obs.height - 3 || harryBot > obs.height + CONFIG.OBSTACLE_GAP + 3)) {
         setRunning(false); setGameOver(true);
       }
     }
     for (const enemy of enemies) {
       // Slightly more forgiving collision detection - add small buffer
       if (enemy.x < harryRight + 2 && enemy.x + CONFIG.ENEMY_W > harryLeft - 2 && enemy.y < harryBot + 2 && enemy.y + CONFIG.ENEMY_H > harryTop - 2) {
         setRunning(false); setGameOver(true);
       }
     }
    setBeams(bs => {
      const newBeams = [...bs];
      setEnemies(enms => enms.filter(enemy => {
        let hit = false;
        for (let i = 0; i < newBeams.length; i++) {
          const beam = newBeams[i];
          if (beam.active && beam.y > enemy.y - 5 && beam.y < enemy.y + CONFIG.ENEMY_H + 5 && beam.x + CONFIG.BEAM_W > enemy.x - 5 && beam.x < enemy.x + CONFIG.ENEMY_W + 5) {
            hit = true; newBeams[i] = { ...beam, active: false }; break;
          }
        }
        return !hit;
      }));
      return newBeams;
    });
    if (playerY <= 0 || playerY + CONFIG.PLAYER_H >= CONFIG.GAME_HEIGHT) { setRunning(false); setGameOver(true); }
  }, [playerY, obstacles, enemies, beams, running]);

  useEffect(() => {
    fetch(`${CONFIG.API}/leaderboard`)
      .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => setLeaderboard([]));
  }, [gameOver]);

  function startGame() {
    setPlayerY(CONFIG.GAME_HEIGHT / 2);
    setVel(0);
    setObstacles([getRandomObstacle(0)]);
    setEnemies([]);
    setBeams([]);
    setScore(0);
    setGameOver(false);
    setRunning(true);
    obstacleId.current = 1;
    enemyId.current = 1;
  }

  function submitScore() {
    fetch(`${CONFIG.API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    }).then(() => fetch(`${CONFIG.API}/leaderboard`))
      .then(res => res.json())
      .then(setLeaderboard);
  }

  return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif" }}>
        Harry Potter: Broomstick Blitz!
      </h2>
      <svg width={CONFIG.GAME_WIDTH} height={CONFIG.GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51" }}>
        {[...Array(24)].map((_, i) => (
          <circle key={i} cx={Math.random()*CONFIG.GAME_WIDTH} cy={Math.random()*CONFIG.GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
        ))}
        {obstacles.map(o => renderObstacle(o, CONFIG.OBSTACLE_W, CONFIG.OBSTACLE_GAP, CONFIG.GAME_HEIGHT))}
        {enemies.map(e => renderEnemy(e, CONFIG.ENEMY_W, CONFIG.ENEMY_H))}
        {renderBeams(beams)}
        {renderHarry(playerY, beams, CONFIG.PLAYER_W, CONFIG.PLAYER_H)}
      </svg>
      <div>Score: {score}</div>
      {!running && (gameOver ? (
        <div>
          <b>Game Over!</b><br />
          <input style={{ margin: 4 }} placeholder="Your name" value={name} onChange={e => setName(e.target.value)} />
          <button onClick={submitScore}>Submit Score</button>
          <button onClick={startGame}>Restart</button>
        </div>
      ) : (
        <button onClick={startGame}>Start Game</button>
      ))}
      <h3>Leaderboard</h3>
      <ol>{leaderboard.map((entry, i) => (
        <li key={i}>{entry.name}: {entry.score}</li>
      ))}</ol>
      <p>Space/click/tap to flap · "A" to cast spell beam · Dodge obstacles/monsters!</p>
    </div>
  );
}
