interface IntroScreenProps {
  onStart: () => void;
  onHelp: () => void;
}

export default function IntroScreen({ onStart, onHelp }: IntroScreenProps) {
    return (
      <div>
        <h3>Welcome to Hogwarts!</h3>
        <p>Fly through obstacles, defeat Witches with magic beams, and achieve the highest score!</p>
        <button onClick={onStart} style={{
          padding: "15px 30px", background: "linear-gradient(45deg, #4CAF50, #45a049)", color: "#fff", border: "none", borderRadius: "8px",
          fontWeight: "bold", cursor: "pointer", fontSize: "16px", boxShadow: "0 6px 12px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
        }}>Start Game</button>
        <button onClick={onHelp} style={{
          marginLeft: "16px", padding: "15px 30px", background: "linear-gradient(45deg, #0097a7, #00bcd4)", color: "#fff", border: "none", borderRadius: "8px",
          fontWeight: "bold", cursor: "pointer", fontSize: "16px", boxShadow: "0 6px 12px rgba(0,0,0,0.2)"
        }}>Get Help</button>
      </div>
    );
  }
