import React, { useRef, useEffect, useState } from "react";
import { renderHarry, renderObstacle, renderEnemy, renderBeams, renderPotion } from "./GameEntities";

const CONFIG = {
  GAME_WIDTH: 400,
  GAME_HEIGHT: 600,
  PLAYER_W: 48,
  PLAYER_H: 48,
  GRAVITY: 0.6,
  FLAP_VEL: -8,
  OBSTACLE_W: 60,
  OBSTACLE_GAP: 180,
  ENEMY_W: 40,
  ENEMY_H: 40,
  BEAM_W: 10,
  POTION_W: 28,
  POTION_H: 38,
  API: "http://localhost:8000",
  ENEMY_COLORS: ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"],
  ENEMY_NAMES: ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"],
};

type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Potion = { x: number; y: number; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

function getRandomObstacle(id: number): Obstacle {
  const min = 50,
    max = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - 50;
  return { x: CONFIG.GAME_WIDTH, height: Math.floor(Math.random() * (max - min) + min), id };
}
function getRandomEnemy(id: number): Enemy {
  const color = CONFIG.ENEMY_COLORS[Math.floor(Math.random() * CONFIG.ENEMY_COLORS.length)];
  const type = CONFIG.ENEMY_NAMES[Math.floor(Math.random() * CONFIG.ENEMY_NAMES.length)];
  const y = Math.floor(Math.random() * (CONFIG.GAME_HEIGHT - CONFIG.ENEMY_H - 20)) + 10;
  return { x: CONFIG.GAME_WIDTH, y, color, type, id };
}
function getRandomPotion(id: number): Potion {
  const y = Math.floor(Math.random() * (CONFIG.GAME_HEIGHT - CONFIG.POTION_H - 40)) + 20;
  return { x: CONFIG.GAME_WIDTH, y, id };
}

export default function App() {
  const [playerY, setPlayerY] = useState(CONFIG.GAME_HEIGHT / 2);
  const [vel, setVel] = useState(0);
  const [obstacles, setObstacles] = useState<Obstacle[]>([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
  const [potions, setPotions] = useState<Potion[]>([]);
  const [score, setScore] = useState(0);
  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
  const [invincible, setInvincible] = useState(false);
  const [invincEnd, setInvincEnd] = useState(0);
  const [invincTimeLeft, setInvincTimeLeft] = useState(0);

  const obstacleId = useRef(1);
  const enemyId = useRef(1);
  const potionId = useRef(1);

  // Main game loop
  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      setVel((v) => Math.min(v + CONFIG.GRAVITY, 10));
      setPlayerY((y) => Math.max(0, Math.min(CONFIG.GAME_HEIGHT - CONFIG.PLAYER_H, y + vel)));
      setScore((s) => s + 1);

      setObstacles((obs) => {
        let nobs = obs.map((o) => ({ ...o, x: o.x - 4 }));
        if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < CONFIG.GAME_WIDTH - 200)) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        return nobs.filter((o) => o.x + CONFIG.OBSTACLE_W > 0);
      });

      setEnemies((enms) => {
        let nenms = enms.map((e) => ({ ...e, x: e.x - 5 }));
        if (Math.random() > 0.97) {
          nenms.push(getRandomEnemy(enemyId.current++));
        }
        return nenms.filter((e) => e.x + CONFIG.ENEMY_W > 0);
      });

      setBeams((bs) =>
        bs.filter((b) => b.active && b.x < CONFIG.GAME_WIDTH).map((b) => ({ ...b, x: b.x + 12 }))
      );

      setPotions((ps) => {
        let nps = ps.map((p) => ({ ...p, x: p.x - 4 }));
        if (Math.random() > 0.995) {
          nps.push(getRandomPotion(potionId.current++));
        }
        return nps.filter((p) => p.x + CONFIG.POTION_W > 0);
      });
    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  // Controls and prevent scroll
  useEffect(() => {
    if (!running) return;
    const handler = (e) => {
      if (["ArrowLeft", "ArrowRight", "ArrowUp", "ArrowDown", " "].includes(e.key)) e.preventDefault();
      if (e.code === "Space") setVel(CONFIG.FLAP_VEL);
      if (e.key === "a" || e.key === "A") setBeams((bs) => [...bs, { x: 30 + CONFIG.PLAYER_W, y: playerY + CONFIG.PLAYER_H / 2 - 4, active: true }]);
      if (e.key === "ArrowUp") setVel(CONFIG.FLAP_VEL);
    };
    window.addEventListener("keydown", handler, { passive: false });
    window.addEventListener("mousedown", () => { if (running) setVel(CONFIG.FLAP_VEL); });
    window.addEventListener("touchstart", () => { if (running) setVel(CONFIG.FLAP_VEL); });
    return () => {
      window.removeEventListener("keydown", handler);
      window.removeEventListener("mousedown", () => {});
      window.removeEventListener("touchstart", () => {});
    };
  }, [playerY, running, vel]);

  // Collision
  useEffect(() => {
    if (!running) return;
    // Potion collision
    setPotions((ps) => {
      let drank = false;
      const res = ps.filter((p) => {
        const overlap =
          30 + CONFIG.PLAYER_W > p.x &&
          30 < p.x + CONFIG.POTION_W &&
          playerY + CONFIG.PLAYER_H > p.y &&
          playerY < p.y + CONFIG.POTION_H;
        if (overlap) drank = true;
        return !overlap;
      });
      if (drank) {
        setInvincible(true);
        setInvincEnd(Date.now() + 10000);
      }
      return res;
    });

    if (invincible) return;

    // Obstacles
    const harryTop = playerY;
    const harryBot = playerY + CONFIG.PLAYER_H;
    const harryLeft = 30;
    const harryRight = 30 + CONFIG.PLAYER_W;
    for (const obs of obstacles) {
      if (
        obs.x < harryRight &&
        obs.x + CONFIG.OBSTACLE_W > harryLeft &&
        (harryTop < obs.height || harryBot > obs.height + CONFIG.OBSTACLE_GAP)
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }
    // Enemies
    for (const enemy of enemies) {
      if (
        enemy.x < harryRight &&
        enemy.x + CONFIG.ENEMY_W > harryLeft &&
        enemy.y < harryBot &&
        enemy.y + CONFIG.ENEMY_H > harryTop
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }
    // Beam to enemy
    setBeams((bs) => {
      const newBeams = [...bs];
      setEnemies((enms) =>
        enms.filter((enemy) => {
          let hit = false;
          for (let i = 0; i < newBeams.length; i++) {
            const beam = newBeams[i];
            if (
              beam.active &&
              beam.y > enemy.y &&
              beam.y < enemy.y + CONFIG.ENEMY_H &&
              beam.x + CONFIG.BEAM_W > enemy.x &&
              beam.x < enemy.x + CONFIG.ENEMY_W
            ) {
              hit = true;
              newBeams[i] = { ...beam, active: false };
              break;
            }
          }
          return !hit;
        })
      );
      return newBeams;
    });

    if (playerY <= 0 || playerY + CONFIG.PLAYER_H >= CONFIG.GAME_HEIGHT) {
      setRunning(false);
      setGameOver(true);
    }
  }, [playerY, obstacles, enemies, beams, potions, running, invincible]);

  // Invincibility timer tracker
  useEffect(() => {
    if (!invincible) {
      setInvincTimeLeft(0);
      return;
    }
    const timer = setInterval(() => {
      const msLeft = invincEnd - Date.now();
      setInvincTimeLeft(Math.max(0, Math.ceil(msLeft / 1000)));
      if (msLeft <= 0) setInvincible(false);
    }, 100);
    return () => clearInterval(timer);
  }, [invincible, invincEnd]);

  useEffect(() => {
    fetch(`${CONFIG.API}/leaderboard`)
      .then((res) => res.json())
      .then(setLeaderboard)
      .catch(() => {});
  }, [gameOver]);

  function startGame() {
    setPlayerY(CONFIG.GAME_HEIGHT / 2); setVel(0);
    setObstacles([getRandomObstacle(0)]); setEnemies([]); setBeams([]); setPotions([]);
    setScore(0); setGameOver(false); setRunning(true);
    setInvincible(false); setInvincEnd(0); setInvincTimeLeft(0);
    obstacleId.current = 1; enemyId.current = 1; potionId.current = 1;
  }
  function submitScore() {
    fetch(`${CONFIG.API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    })
      .then(() => fetch(`${CONFIG.API}/leaderboard`))
      .then((res) => res.json())
      .then(setLeaderboard);
  }

  return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif" }}>
        Harry Potter: Broomstick Blitz!
      </h2>
      {invincible && (
        <div style={{
          color: invincTimeLeft <= 3 ? "#ff4273" : "#fc3",
          fontWeight: "bold",
          fontSize: "22px"
        }}>
          Invincible! {invincTimeLeft}s
        </div>
      )}
      <svg width={CONFIG.GAME_WIDTH} height={CONFIG.GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51" }}>
        {[...Array(24)].map((_, i) => (
          <circle key={i} cx={Math.random()*CONFIG.GAME_WIDTH} cy={Math.random()*CONFIG.GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
        ))}
        {obstacles.map(o => renderObstacle(o, CONFIG.OBSTACLE_W, CONFIG.OBSTACLE_GAP, CONFIG.GAME_HEIGHT))}
        {potions.map(p => renderPotion(p, CONFIG.POTION_W, CONFIG.POTION_H))}
        {enemies.map(e => renderEnemy(e, CONFIG.ENEMY_W, CONFIG.ENEMY_H))}
        {renderBeams(beams, CONFIG.BEAM_W)}
        {renderHarry(playerY, beams, CONFIG.PLAYER_W, CONFIG.PLAYER_H, invincible)}
      </svg>
      <div>Score: {score}</div>
      {!running && (gameOver ? (
        <div>
          <b>Game Over!</b><br />
          <input style={{ margin: 4 }} placeholder="Your name" value={name} onChange={e => setName(e.target.value)} />
          <button onClick={submitScore}>Submit Score</button>
          <button onClick={startGame}>Restart</button>
        </div>
      ) : (
        <button onClick={startGame}>Start Game</button>
      ))}
      <h3>Leaderboard</h3>
      <ol>{leaderboard.map((entry, i) => (
        <li key={i}>{entry.name}: {entry.score}</li>
      ))}</ol>
      <p>Space/click/tap to flap · "A" to cast spell · Dodge obstacles/monsters! <br /> Drink the magical potion for temporary invincibility.</p>
    </div>
  );
}
