import React, { useRef, useEffect, useState } from "react";

const GAME_WIDTH = 400;
const GAME_HEIGHT = 600;
const PLAYER_W = 48;
const PLAYER_H = 48;
const GRAVITY = 0.6;
const FLAP_VEL = -8;
const OBSTACLE_W = 60;
const OBSTACLE_GAP = 180;
const ENEMY_W = 40;
const ENEMY_H = 40;
const BEAM_W = 10;
const API = "http://localhost:8000";

const ENEMY_COLORS = ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"];
const ENEMY_NAMES = ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"];

type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

function getRandomObstacle(id: number): Obstacle {
  const min = 50, max = GAME_HEIGHT - OBSTACLE_GAP - 50;
  return { x: GAME_WIDTH, height: Math.floor(Math.random() * (max - min) + min), id };
}

function getRandomEnemy(id: number): Enemy {
  const color = ENEMY_COLORS[Math.floor(Math.random() * ENEMY_COLORS.length)];
  const type = ENEMY_NAMES[Math.floor(Math.random() * ENEMY_NAMES.length)];
  const y = Math.floor(Math.random() * (GAME_HEIGHT - ENEMY_H - 20)) + 10;
  return { x: GAME_WIDTH, y, color, type, id };
}

export default function App() {
  const [playerY, setPlayerY] = useState(GAME_HEIGHT / 2);
  const [vel, setVel] = useState(0);
  const [obstacles, setObstacles] = useState([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
  const [score, setScore] = useState(0);
  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
  const [showHelp, setShowHelp] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);
  const obstacleId = useRef(1);
  const enemyId = useRef(1);

  // Main loop
  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      setVel(v => Math.min(v + GRAVITY, 10));
      setPlayerY(y => Math.max(0, Math.min(GAME_HEIGHT - PLAYER_H, y + vel)));
      setScore(s => s + 1);

      // Obstacles move left
      setObstacles(obs => {
        let nobs = obs.map(o => ({ ...o, x: o.x - 4 }));
        if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < GAME_WIDTH - 200)) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        return nobs.filter(o => o.x + OBSTACLE_W > 0);
      });

      // Enemies move left
      setEnemies(enms => {
        let nenms = enms.map(e => ({ ...e, x: e.x - 5 }));
        // Spawn random enemies
        if (Math.random() > 0.97) {
          nenms.push(getRandomEnemy(enemyId.current++));
        }
        return nenms.filter(e => e.x + ENEMY_W > 0);
      });

      // Beam movement and hit detection
      setBeams(bs =>
        bs
          .filter(b => b.active && b.x < GAME_WIDTH)
          .map(b => ({ ...b, x: b.x + 12 }))
      );

    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  // Flap/beam controls
  useEffect(() => {
    if (!running) return;

    const flap = () => setVel(FLAP_VEL);
    const cast = () => {
      setBeams(bs => [
        ...bs,
        { x: 30 + PLAYER_W, y: playerY + PLAYER_H / 2 - 4, active: true }
      ]);
    };

     const onKey = (e: KeyboardEvent) => {
       if (e.code === "Space") {
         e.preventDefault(); // Prevent page scroll
         flap();
       }
       if (e.key === "a" || e.key === "A") cast();
       if (e.key === "ArrowUp") setVel(FLAP_VEL);
     };
    window.addEventListener("keydown", onKey);
    window.addEventListener("mousedown", flap);
    window.addEventListener("touchstart", flap);
    return () => {
      window.removeEventListener("keydown", onKey);
      window.removeEventListener("mousedown", flap);
      window.removeEventListener("touchstart", flap);
    };
  }, [playerY, running]);

  // Collision, enemy/beam logic, and game over
  useEffect(() => {
    if (!running) return;

    // Collisions with obstacles
    const harryTop = playerY;
    const harryBot = playerY + PLAYER_H;
    const harryLeft = 30;
    const harryRight = 30 + PLAYER_W;

    // Flappy barrier collision: only check at Harry's X
    for (const obs of obstacles) {
      if (
        obs.x < harryRight &&
        obs.x + OBSTACLE_W > harryLeft &&
        (harryTop < obs.height || harryBot > obs.height + OBSTACLE_GAP)
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }

    // Collisions with any enemy
    for (const enemy of enemies) {
      if (
        enemy.x < harryRight &&
        enemy.x + ENEMY_W > harryLeft &&
        enemy.y < harryBot &&
        enemy.y + ENEMY_H > harryTop
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }

     // Beam to enemy collision - check each beam against each enemy
     setBeams(bs => {
       const newBeams = [...bs];
       setEnemies(enms => {
         return enms.filter(enemy => {
           let hit = false;
           for (let i = 0; i < newBeams.length; i++) {
             const beam = newBeams[i];
             if (
               beam.active &&
               beam.y > enemy.y && 
               beam.y < enemy.y + ENEMY_H &&
               beam.x + BEAM_W > enemy.x && 
               beam.x < enemy.x + ENEMY_W
             ) {
               hit = true;
               newBeams[i] = { ...beam, active: false }; // Deactivate the beam
               break;
             }
           }
           return !hit; // Remove enemy if hit
         });
       });
       return newBeams;
     });

    // Out of bounds
    if (playerY <= 0 || playerY + PLAYER_H >= GAME_HEIGHT) {
      setRunning(false);
      setGameOver(true);
    }
    // eslint-disable-next-line
  }, [playerY, obstacles, enemies, beams, running]);

  // Leaderboard fetch
  useEffect(() => {
    fetch(`${API}/leaderboard`)
      .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => {});
  }, [gameOver]);

  function startGame() {
    setPlayerY(GAME_HEIGHT / 2);
    setVel(0);
    setObstacles([getRandomObstacle(0)]);
    setEnemies([]);
    setBeams([]);
    setScore(0);
    setGameOver(false);
    setRunning(true);
    obstacleId.current = 1;
    enemyId.current = 1;
  }

  function submitScore() {
    fetch(`${API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    })
      .then(() => fetch(`${API}/leaderboard`))
      .then(res => res.json())
      .then(setLeaderboard);
  }

  // Drawing Harry Potter (much more detailed)
  function renderHarry() {
    const centerX = 30 + PLAYER_W/2;
    const centerY = playerY + PLAYER_H/2;

    return (
      <g>
        {/* Broomstick - more detailed */}
        <ellipse cx={centerX} cy={playerY + PLAYER_H - 8} rx={PLAYER_W/2 + 4} ry={3} fill="#8B4513"/>
        <rect x={centerX - 2} y={playerY + PLAYER_H - 12} width={4} height={8} fill="#654321"/>
        <rect x={centerX - 1} y={playerY + PLAYER_H - 16} width={2} height={6} fill="#8B4513"/>

        {/* Harry's head */}
        <circle cx={centerX} cy={playerY + 18} r={18} fill="#F4C2A1" stroke="#D4A574" strokeWidth={2}/>

        {/* Hair - messy black hair */}
        <path d={`M ${centerX - 15} ${playerY + 8} Q ${centerX - 8} ${playerY + 2} ${centerX} ${playerY + 6} Q ${centerX + 8} ${playerY + 2} ${centerX + 15} ${playerY + 8} Q ${centerX + 12} ${playerY + 12} ${centerX + 8} ${playerY + 10} Q ${centerX + 4} ${playerY + 8} ${centerX} ${playerY + 10} Q ${centerX - 4} ${playerY + 8} ${centerX - 8} ${playerY + 10} Q ${centerX - 12} ${playerY + 12} ${centerX - 15} ${playerY + 8} Z`} fill="#2C1810"/>

        {/* Glasses - round spectacles */}
        <circle cx={centerX - 6} cy={playerY + 18} r={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none"/>
        <circle cx={centerX + 6} cy={playerY + 18} r={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none"/>
        <line x1={centerX - 1} y1={playerY + 18} x2={centerX + 1} y2={playerY + 18} stroke="#2C2C2C" strokeWidth={2}/>

        {/* Eyes */}
        <circle cx={centerX - 6} cy={playerY + 18} r={2} fill="#2C2C2C"/>
        <circle cx={centerX + 6} cy={playerY + 18} r={2} fill="#2C2C2C"/>
        <circle cx={centerX - 5} cy={playerY + 17} r={0.8} fill="white"/>
        <circle cx={centerX + 7} cy={playerY + 17} r={0.8} fill="white"/>

        {/* Nose */}
        <ellipse cx={centerX} cy={playerY + 22} rx={1.5} ry={2} fill="#E6B89C"/>

        {/* Mouth - slight smile */}
        <path d={`M ${centerX - 3} ${playerY + 26} Q ${centerX} ${playerY + 28} ${centerX + 3} ${playerY + 26}`} stroke="#2C2C2C" strokeWidth={1.5} fill="none"/>

        {/* Lightning scar - more detailed */}
        <path d={`M ${centerX - 4} ${playerY + 8} L ${centerX - 1} ${playerY + 12} L ${centerX + 1} ${playerY + 10} L ${centerX + 4} ${playerY + 14} L ${centerX + 2} ${playerY + 16} L ${centerX + 5} ${playerY + 18}`} stroke="#FFD700" strokeWidth={2.5} fill="none"/>
        <path d={`M ${centerX - 3} ${playerY + 9} L ${centerX} ${playerY + 13} L ${centerX + 2} ${playerY + 11} L ${centerX + 3} ${playerY + 15}`} stroke="#FFA500" strokeWidth={1.5} fill="none"/>

        {/* Robes - Gryffindor colors */}
        <rect x={30 + 8} y={playerY + 36} width={PLAYER_W - 16} height={12} fill="#7F0000" rx={6}/>
        <rect x={30 + 10} y={playerY + 38} width={PLAYER_W - 20} height={8} fill="#FFD700" rx={4}/>

        {/* Wand - more detailed with tip */}
        <rect x={30 + PLAYER_W - 2} y={playerY + PLAYER_H/2 - 1} width={20} height={2} fill="#8B4513" rx={1}/>
        <circle cx={30 + PLAYER_W + 18} cy={playerY + PLAYER_H/2} r={1.5} fill="#FFD700"/>
        <circle cx={30 + PLAYER_W + 20} cy={playerY + PLAYER_H/2} r={0.8} fill="#FF6B6B"/>

        {/* Wand sparkles when shooting */}
        {beams.some(b => b.active) && (
          <g>
            <circle cx={30 + PLAYER_W + 22} cy={playerY + PLAYER_H/2 - 3} r={1} fill="#FFD700" opacity={0.8}/>
            <circle cx={30 + PLAYER_W + 24} cy={playerY + PLAYER_H/2 + 2} r={0.8} fill="#FF6B6B" opacity={0.6}/>
            <circle cx={30 + PLAYER_W + 20} cy={playerY + PLAYER_H/2 - 5} r={0.6} fill="#FFFFFF" opacity={0.9}/>
          </g>
        )}
      </g>
    );
  }

  // Drawing Enemies - more detailed Harry Potter creatures
  function renderEnemy(e: Enemy) {
    const centerX = e.x + ENEMY_W / 2;
    const centerY = e.y + ENEMY_H / 2;

    return (
      <g key={e.id}>
        {/* Main body */}
        <ellipse cx={centerX} cy={centerY + 4} rx={ENEMY_W/2 - 2} ry={ENEMY_H/2 - 2} fill={e.color} stroke="black" strokeWidth={1.5}/>

        {/* Creature-specific details based on type */}
        {e.type === "Dementor" && (
          <g>
            {/* Hood */}
            <path d={`M ${e.x + 4} ${e.y + 8} Q ${centerX} ${e.y + 2} ${e.x + ENEMY_W - 4} ${e.y + 8} L ${e.x + ENEMY_W - 2} ${e.y + 12} Q ${centerX} ${e.y + 6} ${e.x + 2} ${e.y + 12} Z`} fill="#1a1a1a"/>
            {/* Eyes */}
            <circle cx={centerX - 4} cy={e.y + 6} r={1.5} fill="#ff0000"/>
            <circle cx={centerX + 4} cy={e.y + 6} r={1.5} fill="#ff0000"/>
          </g>
        )}

        {e.type === "Death Eater" && (
          <g>
            {/* Mask */}
            <ellipse cx={centerX} cy={centerY - 2} rx={8} ry={6} fill="#2c2c2c"/>
            {/* Eyes */}
            <circle cx={centerX - 3} cy={centerY - 3} r={1} fill="#ff0000"/>
            <circle cx={centerX + 3} cy={centerY - 3} r={1} fill="#ff0000"/>
            {/* Dark mark on arm */}
            <path d={`M ${e.x + 2} ${e.y + 8} L ${e.x + 6} ${e.y + 6} L ${e.x + 8} ${e.y + 10} L ${e.x + 4} ${e.y + 12} Z`} fill="#8B0000"/>
          </g>
        )}

        {e.type === "Troll" && (
          <g>
            {/* Big nose */}
            <ellipse cx={centerX} cy={centerY - 1} rx={3} ry={2} fill="#8B4513"/>
            {/* Small eyes */}
            <circle cx={centerX - 2} cy={centerY - 4} r={1} fill="black"/>
            <circle cx={centerX + 2} cy={centerY - 4} r={1} fill="black"/>
            {/* Club */}
            <rect x={e.x + ENEMY_W - 2} y={e.y + 2} width={4} height={ENEMY_H - 4} fill="#654321"/>
            <circle cx={e.x + ENEMY_W} cy={e.y + 1} r={2} fill="#8B4513"/>
          </g>
        )}

        {e.type === "Acromantula" && (
          <g>
            {/* Spider body */}
            <ellipse cx={centerX} cy={centerY} rx={6} ry={4} fill={e.color}/>
            {/* Legs */}
            {[...Array(8)].map((_, i) => {
              const angle = (i * Math.PI) / 4;
              const startX = centerX + Math.cos(angle) * 6;
              const startY = centerY + Math.sin(angle) * 4;
              const endX = startX + Math.cos(angle) * 8;
              const endY = startY + Math.sin(angle) * 8;
              return <line key={i} x1={startX} y1={startY} x2={endX} y2={endY} stroke="black" strokeWidth={1.5}/>;
            })}
            {/* Eyes */}
            <circle cx={centerX - 2} cy={centerY - 2} r={1} fill="red"/>
            <circle cx={centerX + 2} cy={centerY - 2} r={1} fill="red"/>
          </g>
        )}

        {e.type === "Werewolf" && (
          <g>
            {/* Snout */}
            <ellipse cx={centerX} cy={centerY + 2} rx={4} ry={3} fill="#8B4513"/>
            {/* Ears */}
            <ellipse cx={centerX - 3} cy={centerY - 4} rx={2} ry={3} fill="#654321"/>
            <ellipse cx={centerX + 3} cy={centerY - 4} rx={2} ry={3} fill="#654321"/>
            {/* Eyes */}
            <circle cx={centerX - 2} cy={centerY - 1} r={1} fill="yellow"/>
            <circle cx={centerX + 2} cy={centerY - 1} r={1} fill="yellow"/>
            {/* Teeth */}
            <path d={`M ${centerX - 1} ${centerY + 4} L ${centerX} ${centerY + 6} L ${centerX + 1} ${centerY + 4}`} fill="white"/>
          </g>
        )}

        {/* Name label */}
        <text x={centerX} y={e.y - 4} fontSize="10" fill="#fff" textAnchor="middle" stroke="black" strokeWidth={0.5}>
          {e.type}
        </text>
      </g>
    );
  }

  // Drawing Obstacles (magic barriers/trees/etc)
  function renderObstacle(o: Obstacle) {
    return (
      <g key={o.id}>
        {/* Top barrier */}
        <rect x={o.x} y={0} width={OBSTACLE_W} height={o.height} fill="#b8c2ff" />
        {/* Bottom barrier */}
        <rect x={o.x} y={o.height + OBSTACLE_GAP} width={OBSTACLE_W} height={GAME_HEIGHT - o.height - OBSTACLE_GAP} fill="#b8c2ff" />
        {/* Spark upgrades: add potion, star, etc here for bonus later */}
      </g>
    );
  }

  // Drawing Beams
  function renderBeams() {
    return beams.filter(b => b.active).map((b, i) =>
      <rect key={i} x={b.x} y={b.y} width={24} height={6} fill="#ffd700" rx={3} />
    );
  }

  return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif" }}>
        Harry Potter: Broomstick Blitz!
      </h2>
      <svg width={GAME_WIDTH} height={GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51" }}>
        {/* Starry sky */}
        {[...Array(24)].map((_, i) => (
          <circle key={i} cx={Math.random()*GAME_WIDTH} cy={Math.random()*GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
        ))}
        {/* Obstacles */}
        {obstacles.map(renderObstacle)}
        {/* Enemies */}
        {enemies.map(renderEnemy)}
        {/* Beams */}
        {renderBeams()}
        {/* Harry */}
        {renderHarry()}
      </svg>
      <div style={{ fontSize: 22, margin: "8px 0" }}>
        Score: {score}
      </div>
      {!running && (gameOver ? (
        <div>
          <b style={{ fontSize: 28, color: "#edd13b" }}>Game Over!</b><br />
          <input
            style={{ margin: 4, fontSize: 18, padding: 4, borderRadius: 5 }}
            placeholder="Your name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <button style={{ fontSize: 18 }} onClick={submitScore}>Submit Score</button>
          <button style={{ fontSize: 18, marginLeft: 8 }} onClick={startGame}>Restart</button>
        </div>
      ) : (
        <button style={{ fontSize: 22, marginTop: 8 }} onClick={startGame}>Start Game</button>
      ))}
      <h3 style={{ color: "#ffb400" }}>Leaderboard</h3>
      <ol style={{ maxWidth: 300, margin: "0 auto", background: "rgba(255,255,255,0.15)", borderRadius: 8, fontSize: 20 }}>
        {leaderboard.map((entry, i) => (
          <li key={i}>
            <span style={{color: "#f1ee04"}}>{entry.name}</span>: {entry.score}
          </li>
        ))}
      </ol>
      <p style={{ marginTop: 12, fontSize: 18 }}>
        Controls: <b>Space/Click/Tap</b> to fly  <b>A</b> to cast spell<br/>
        Dodge barriers and monsters, zap enemies with your wand!
      </p>
    </div>
  );
}
