import React from 'react';

interface ScoreEntry {
  name: string;
  score: number;
}

interface LeaderboardProps {
  leaderboard: ScoreEntry[];
}

export default function Leaderboard({ leaderboard }: LeaderboardProps) {
  return (
    <div style={{
      minWidth: "300px", 
      background: "linear-gradient(135deg, #1A0E3D, #2D1B69, #1C1C1C)", 
      padding: "30px", 
      borderRadius: "20px", 
      border: "3px solid #D4AF37",
      boxShadow: "0 12px 40px rgba(0,0,0,0.6), inset 0 1px 0 rgba(212, 175, 55, 0.2)", 
      backdropFilter: "blur(15px)",
      marginLeft: "25px"
    }}>
      <div style={{ 
        fontSize: "28px", 
        color: "#D4AF37", 
        marginBottom: "25px", 
        textAlign: "center",
        textShadow: "3px 3px 6px rgba(0,0,0,0.8), 0 0 15px rgba(212, 175, 55, 0.4)", 
        fontWeight: "bold",
        letterSpacing: "1px"
      }}>
        üèÜ Leaderboard
      </div>
      {leaderboard.length === 0 ? (
        <div style={{ 
          color: "#C0C0C0", 
          textAlign: "center", 
          fontStyle: "italic", 
          padding: "40px 0", 
          fontSize: "18px",
          textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
        }}>
          No scores yet
        </div>
      ) : (
        <div style={{ maxHeight: "400px", overflowY: "auto" }}>
          {leaderboard.slice(0, 10).map((entry, i) => (
            <div key={i} style={{
              display: "flex", justifyContent: "space-between", alignItems: "center",
              padding: "15px 20px", margin: "10px 0", borderRadius: "12px",
              background: i < 3 ? "linear-gradient(135deg, rgba(212, 175, 55, 0.2), rgba(184, 134, 11, 0.1))" : "linear-gradient(135deg, rgba(45, 27, 105, 0.3), rgba(26, 14, 61, 0.2))",
              border: i < 3 ? "2px solid rgba(212, 175, 55, 0.6)" : "2px solid rgba(212, 175, 55, 0.3)",
              transition: "all 0.3s ease",
              boxShadow: i < 3 ? "0 4px 15px rgba(212, 175, 55, 0.2)" : "0 2px 8px rgba(0,0,0,0.3)"
            }}>
              <div style={{
                display: "flex", alignItems: "center", gap: "12px"
              }}>
                <span style={{
                  fontSize: "20px", fontWeight: "bold",
                  color: i === 0 ? "#D4AF37" : i === 1 ? "#C0C0C0" : i === 2 ? "#B8860B" : "#C0C0C0",
                  textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
                }}>
                  {i === 0 ? "ü•á" : i === 1 ? "ü•à" : i === 2 ? "ü•â" : `${i + 1}.`}
                </span>
                <span style={{
                  color: i === 0 ? "#D4AF37" : i === 1 ? "#C0C0C0" : i === 2 ? "#B8860B" : "#C0C0C0",
                  fontWeight: i < 3 ? "bold" : "normal", 
                  fontSize: i < 3 ? "18px" : "16px",
                  textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
                }}>
                  {entry.name}
                </span>
              </div>
              <span style={{
                color: "#D4AF37", 
                fontWeight: "bold", 
                fontSize: i < 3 ? "18px" : "16px",
                textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
              }}>
                {entry.score.toLocaleString()}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
