import React, { useRef, useEffect, useState } from "react";
import { renderHarry, renderObstacle, renderEnemy, renderBeams } from "./GameEntities";

// Game configuration constants
const CONFIG = {
  GAME_WIDTH: 400,           // Width of the game canvas
  GAME_HEIGHT: 600,          // Height of the game canvas
  PLAYER_W: 48,              // Harry's width
  PLAYER_H: 48,              // Harry's height
  GRAVITY: 0.6,              // Gravity force pulling Harry down
  FLAP_VEL: -8,              // Velocity when Harry flaps (negative = up)
  OBSTACLE_W: 60,            // Width of obstacles
  OBSTACLE_GAP: 220,         // Gap between top and bottom obstacles (increased for easier passage)
  ENEMY_W: 40,               // Enemy width
  ENEMY_H: 40,               // Enemy height
  BEAM_W: 10,                // Magic beam width
  API: "http://localhost:8000" // Backend API URL
};

// Type definitions for game entities
type Obstacle = { x: number; height: number; id: number }; // Obstacle with position and height
type Enemy = { x: number; y: number; color: string; type: string; id: number }; // Enemy with position, color, and type
type Beam = { x: number; y: number; active: boolean }; // Magic beam with position and active state
type ScoreEntry = { name: string; score: number }; // Leaderboard entry

// Generate a random obstacle with safe passage guaranteed
function getRandomObstacle(id: number): Obstacle {
  // Make obstacles much smaller for easier passage
  const safeMin = 10; // Minimum obstacle height
  const safeMax = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - 10; // Maximum obstacle height
  const height = Math.floor(Math.random() * (safeMax - safeMin) + safeMin);

  // Ensure gap is always very large for easy passage
  const minGapSize = CONFIG.PLAYER_H + 60; // Even larger buffer for very easy escape

  // Calculate actual gap size
  const actualGapSize = CONFIG.OBSTACLE_GAP;

  if (actualGapSize < minGapSize) {
    // Adjust height to ensure minimum gap - make it very generous
    const adjustedHeight = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - minGapSize;
    return { x: CONFIG.GAME_WIDTH, height: Math.max(safeMin, adjustedHeight), id };
  }

  return { x: CONFIG.GAME_WIDTH, height, id };
}

// Generate a Dementor enemy
function getRandomEnemy(id: number): Enemy {
  const y = Math.floor(Math.random() * (CONFIG.GAME_HEIGHT - CONFIG.ENEMY_H - 20)) + 10;
  return { x: CONFIG.GAME_WIDTH, y, color: "#1a1a1a", type: "Dementor", id };
}

// Main game component
export default function App() {
  // Game state variables
  const [playerY, setPlayerY] = useState(CONFIG.GAME_HEIGHT / 2); // Harry's vertical position
  const [vel, setVel] = useState(0); // Harry's vertical velocity
  const [obstacles, setObstacles] = useState([getRandomObstacle(0)]); // Array of obstacles
  const [enemies, setEnemies] = useState<Enemy[]>([]); // Array of enemies
  const [beams, setBeams] = useState<Beam[]>([]); // Array of magic beams
  const [score, setScore] = useState(0); // Current score
  const [running, setRunning] = useState(false); // Whether game is running
  const [gameOver, setGameOver] = useState(false); // Whether game is over
  const [name, setName] = useState(""); // Player name for leaderboard
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]); // Leaderboard data
  const [showIntro, setShowIntro] = useState(true); // Whether to show intro screen
  const [showHelp, setShowHelp] = useState(false); // Whether to show help modal

  // Refs for generating unique IDs
  const obstacleId = useRef(1);
  const enemyId = useRef(1);

  // Main game loop - runs every 24ms when game is running
  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      // Apply gravity to Harry's velocity (capped at 10)
      setVel(v => Math.min(v + CONFIG.GRAVITY, 10));
      // Update Harry's position based on velocity, keeping him within screen bounds
      setPlayerY(y => Math.max(0, Math.min(CONFIG.GAME_HEIGHT - CONFIG.PLAYER_H, y + vel)));
      // Increment score
      setScore(s => s + 1);

      // Move obstacles left and spawn new ones
      setObstacles(obs => {
        let nobs = obs.map(o => ({ ...o, x: o.x - 3 })); // Slower obstacle movement (was 4, now 3)
        // Spawn new obstacle if none exist or last one is far enough
        if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < CONFIG.GAME_WIDTH - 200)) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        // Remove obstacles that have moved off screen
        return nobs.filter(o => o.x + CONFIG.OBSTACLE_W > 0);
      });

      // Move enemies left and spawn new ones
      setEnemies(enms => {
        let nenms = enms.map(e => ({ ...e, x: e.x - 5 }));
        // Spawn new enemy with reduced probability (0.985 = 1.5% chance per frame)
        if (Math.random() > 0.985) { // Reduced spawn rate from 0.97 to 0.985
          nenms.push(getRandomEnemy(enemyId.current++));
        }
        // Remove enemies that have moved off screen
        return nenms.filter(e => e.x + CONFIG.ENEMY_W > 0);
      });

      // Move active beams right and remove off-screen beams
      setBeams(bs => bs.filter(b => b.active && b.x < CONFIG.GAME_WIDTH).map(b => ({ ...b, x: b.x + 16 }))); // Faster beam speed
    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  // Controls and input handling
  useEffect(() => {
    if (!running) return;

    // Function to make Harry flap (jump up)
    const flap = () => setVel(CONFIG.FLAP_VEL);

    // Function to cast a magic beam
    const cast = () => setBeams(bs => [...bs, { x: 30 + CONFIG.PLAYER_W, y: playerY + CONFIG.PLAYER_H / 2 - 4, active: true }]);

    // Keyboard event handler
    const onKey = (e: KeyboardEvent) => {
      // Prevent page scrolling for arrow keys when game is running
      if (e.key === "ArrowLeft" || e.key === "ArrowRight" || e.key === "ArrowUp" || e.key === "ArrowDown") {
        e.preventDefault();
      }

      // Space bar: flap (prevent default to stop page scroll)
      if (e.code === "Space") { e.preventDefault(); flap(); }
      // A key: cast magic beam
      if (e.key === "a" || e.key === "A") cast();
      // Up arrow: also flap
      if (e.key === "ArrowUp") setVel(CONFIG.FLAP_VEL);
    };

    // Add event listeners
    window.addEventListener("keydown", onKey);
    window.addEventListener("mousedown", flap); // Click to flap
    window.addEventListener("touchstart", flap); // Touch to flap

    // Cleanup event listeners
    return () => {
      window.removeEventListener("keydown", onKey);
      window.removeEventListener("mousedown", flap);
      window.removeEventListener("touchstart", flap);
    };
  }, [playerY, running]);

  // Collision detection and game over conditions
  useEffect(() => {
    if (!running) return;

    // Calculate Harry's bounding box
    const harryTop = playerY, harryBot = playerY + CONFIG.PLAYER_H, harryLeft = 30, harryRight = 30 + CONFIG.PLAYER_W;

    // Check collision with obstacles
    for (const obs of obstacles) {
      // More forgiving obstacle collision - add small buffer
      if (obs.x < harryRight + 3 && obs.x + CONFIG.OBSTACLE_W > harryLeft - 3 && (harryTop < obs.height - 3 || harryBot > obs.height + CONFIG.OBSTACLE_GAP + 3)) {
        setRunning(false); setGameOver(true);
      }
    }

    // Check collision with enemies
    for (const enemy of enemies) {
      // Slightly more forgiving collision detection - add small buffer
      if (enemy.x < harryRight + 2 && enemy.x + CONFIG.ENEMY_W > harryLeft - 2 && enemy.y < harryBot + 2 && enemy.y + CONFIG.ENEMY_H > harryTop - 2) {
        setRunning(false); setGameOver(true);
      }
    }

    // Check beam-to-enemy collisions and remove hit enemies
    setBeams(bs => {
      const newBeams = [...bs];
      setEnemies(enms => enms.filter(enemy => {
        let hit = false;
        for (let i = 0; i < newBeams.length; i++) {
          const beam = newBeams[i];
          // Check if beam hits enemy (with generous hit detection)
          if (beam.active && beam.y > enemy.y - 5 && beam.y < enemy.y + CONFIG.ENEMY_H + 5 && beam.x + CONFIG.BEAM_W > enemy.x - 5 && beam.x < enemy.x + CONFIG.ENEMY_W + 5) {
            hit = true; newBeams[i] = { ...beam, active: false }; break;
          }
        }
        return !hit; // Keep enemy if not hit
      }));
      return newBeams;
    });

    // Check if Harry hits top or bottom of screen
    if (playerY <= 0 || playerY + CONFIG.PLAYER_H >= CONFIG.GAME_HEIGHT) { 
      setRunning(false); setGameOver(true); 
    }
  }, [playerY, obstacles, enemies, beams, running]);

  // Fetch leaderboard when game ends
  useEffect(() => {
    fetch(`${CONFIG.API}/leaderboard`)
      .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => setLeaderboard([]));
  }, [gameOver]);

  // Reset game state and start new game
  function startGame() {
    setPlayerY(CONFIG.GAME_HEIGHT / 2); // Reset Harry to center
    setVel(0); // Reset velocity
    setObstacles([getRandomObstacle(0)]); // Start with one obstacle
    setEnemies([]); // Clear enemies
    setBeams([]); // Clear beams
    setScore(0); // Reset score
    setGameOver(false); // Reset game over state
    setRunning(true); // Start the game
    setShowIntro(false); // Hide intro screen
    obstacleId.current = 1; // Reset obstacle ID counter
    enemyId.current = 1; // Reset enemy ID counter
  }

  // Submit score to leaderboard
  function submitScore() {
    fetch(`${CONFIG.API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    }).then(() => fetch(`${CONFIG.API}/leaderboard`))
      .then(res => res.json())
      .then(setLeaderboard);
  }

  return (
    <div style={{ fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff", display: "flex", flexDirection: "column", alignItems: "center" }}>
      {/* Game title */}
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif", textAlign: "center" }}>
        Harry Potter: Broomstick Blitz!
      </h2>

      {/* Intro Screen */}
      {showIntro && (
        <div style={{ 
          display: "flex", 
          flexDirection: "column", 
          alignItems: "center", 
          justifyContent: "center", 
          minHeight: "60vh",
          gap: "30px"
        }}>
          <div style={{ 
            background: "rgba(0,0,0,0.8)", 
            padding: "40px", 
            borderRadius: "15px", 
            border: "3px solid #f3ce13",
            textAlign: "center",
            maxWidth: "500px"
          }}>
            <h3 style={{ 
              color: "#f3ce13", 
              margin: "0 0 20px 0", 
              fontSize: "24px",
              textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
            }}>
              Welcome to Hogwarts!
            </h3>
            <p style={{ 
              color: "#fff", 
              margin: "0 0 30px 0", 
              fontSize: "16px",
              lineHeight: "1.5"
            }}>
              Fly through obstacles, defeat Dementors with magic beams, and achieve the highest score!
            </p>
            <div style={{ display: "flex", gap: "20px", justifyContent: "center" }}>
              <button 
                onClick={startGame}
                style={{
                  padding: "15px 30px",
                  background: "linear-gradient(45deg, #f3ce13, #ffd700)",
                  color: "#000",
                  border: "none",
                  borderRadius: "8px",
                  fontWeight: "bold",
                  cursor: "pointer",
                  fontSize: "16px",
                  boxShadow: "0 6px 12px rgba(0,0,0,0.3)",
                  textTransform: "uppercase",
                  letterSpacing: "1px"
                }}
              >
                Start Game
              </button>
              <button 
                onClick={() => setShowHelp(true)}
                style={{
                  padding: "15px 30px",
                  background: "linear-gradient(45deg, #4CAF50, #45a049)",
                  color: "#fff",
                  border: "none",
                  borderRadius: "8px",
                  fontWeight: "bold",
                  cursor: "pointer",
                  fontSize: "16px",
                  boxShadow: "0 6px 12px rgba(0,0,0,0.3)",
                  textTransform: "uppercase",
                  letterSpacing: "1px"
                }}
              >
                Get Help
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Help Modal */}
      {showHelp && (
        <div style={{ 
          position: "fixed", 
          top: 0, 
          left: 0, 
          right: 0, 
          bottom: 0, 
          background: "rgba(0,0,0,0.8)", 
          display: "flex", 
          alignItems: "center", 
          justifyContent: "center",
          zIndex: 1000
        }}>
          <div style={{ 
            background: "rgba(0,0,0,0.9)", 
            padding: "30px", 
            borderRadius: "15px", 
            border: "3px solid #f3ce13",
            maxWidth: "500px",
            margin: "20px"
          }}>
            <h3 style={{ 
              color: "#f3ce13", 
              margin: "0 0 20px 0", 
              fontSize: "20px",
              textAlign: "center"
            }}>
              How to Play
            </h3>
            <div style={{ color: "#fff", lineHeight: "1.6" }}>
              <p><strong>Controls:</strong></p>
              <ul style={{ margin: "10px 0", paddingLeft: "20px" }}>
                <li><strong>Space Bar</strong> or <strong>Click/Tap</strong> - Make Harry flap (jump up)</li>
                <li><strong>A Key</strong> - Cast magic beam to defeat Dementors</li>
                <li><strong>Up Arrow</strong> - Alternative flap control</li>
              </ul>
              <p><strong>Objective:</strong></p>
              <ul style={{ margin: "10px 0", paddingLeft: "20px" }}>
                <li>Fly through the gaps between obstacles</li>
                <li>Defeat Dementors with magic beams</li>
                <li>Avoid hitting obstacles or Dementors</li>
                <li>Score points by surviving and defeating enemies</li>
              </ul>
              <p><strong>Tips:</strong></p>
              <ul style={{ margin: "10px 0", paddingLeft: "20px" }}>
                <li>Time your flaps to maintain steady flight</li>
                <li>Use magic beams strategically to clear Dementors</li>
                <li>Stay in the middle of the screen for better control</li>
              </ul>
            </div>
            <div style={{ textAlign: "center", marginTop: "20px" }}>
              <button 
                onClick={() => setShowHelp(false)}
                style={{
                  padding: "10px 25px",
                  background: "linear-gradient(45deg, #f3ce13, #ffd700)",
                  color: "#000",
                  border: "none",
                  borderRadius: "5px",
                  fontWeight: "bold",
                  cursor: "pointer",
                  fontSize: "14px",
                  boxShadow: "0 4px 8px rgba(0,0,0,0.3)"
                }}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Main game area with leaderboard - only show when not in intro */}
      {!showIntro && (
        <div style={{ display: "flex", alignItems: "flex-start", justifyContent: "space-between", width: "100%", maxWidth: "1200px", margin: "0 auto" }}>
          {/* Left spacer */}
          <div style={{ flex: "1" }}></div>

          {/* Center - Game area */}
          <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
            {/* Game canvas */}
            <svg width={CONFIG.GAME_WIDTH} height={CONFIG.GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51", borderRadius: "8px" }}>
              {/* Background stars */}
              {[...Array(24)].map((_, i) => (
                <circle key={i} cx={Math.random()*CONFIG.GAME_WIDTH} cy={Math.random()*CONFIG.GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
              ))}
              {/* Render game entities */}
              {obstacles.map(o => renderObstacle(o, CONFIG.OBSTACLE_W, CONFIG.OBSTACLE_GAP, CONFIG.GAME_HEIGHT))}
              {enemies.map(e => renderEnemy(e, CONFIG.ENEMY_W, CONFIG.ENEMY_H))}
              {renderBeams(beams)}
              {renderHarry(playerY, beams, CONFIG.PLAYER_W, CONFIG.PLAYER_H)}
            </svg>

            {/* Score display */}
            <div style={{ 
              margin: "10px 0", 
              fontSize: "18px", 
              fontWeight: "bold", 
              color: "#f3ce13",
              textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
            }}>
              Score: {score}
            </div>

            {/* Game over screen */}
            {!running && gameOver && (
              <div style={{ 
                background: "rgba(0,0,0,0.8)", 
                padding: "20px", 
                borderRadius: "10px", 
                border: "2px solid #f3ce13",
                textAlign: "center",
                marginTop: "10px"
              }}>
                <h3 style={{ color: "#ff6b6b", margin: "0 0 15px 0", fontSize: "20px" }}>Game Over!</h3>
                <div style={{ marginBottom: "15px" }}>
                  <input 
                    style={{ 
                      padding: "8px 12px", 
                      borderRadius: "5px", 
                      border: "2px solid #f3ce13", 
                      background: "#2d2d2d", 
                      color: "#fff", 
                      fontSize: "14px",
                      width: "200px",
                      textAlign: "center"
                    }} 
                    placeholder="Enter your name" 
                    value={name} 
                    onChange={e => setName(e.target.value)} 
                  />
                </div>
                <div style={{ display: "flex", gap: "10px", justifyContent: "center" }}>
                  <button 
                    onClick={submitScore}
                    style={{
                      padding: "10px 20px",
                      background: "linear-gradient(45deg, #f3ce13, #ffd700)",
                      color: "#000",
                      border: "none",
                      borderRadius: "5px",
                      fontWeight: "bold",
                      cursor: "pointer",
                      fontSize: "14px",
                      boxShadow: "0 4px 8px rgba(0,0,0,0.3)"
                    }}
                  >
                    Submit Score
                  </button>
                  <button 
                    onClick={() => { setShowIntro(true); setGameOver(false); }}
                    style={{
                      padding: "10px 20px",
                      background: "linear-gradient(45deg, #4CAF50, #45a049)",
                      color: "#fff",
                      border: "none",
                      borderRadius: "5px",
                      fontWeight: "bold",
                      cursor: "pointer",
                      fontSize: "14px",
                      boxShadow: "0 4px 8px rgba(0,0,0,0.3)"
                    }}
                  >
                    Main Menu
                  </button>
                </div>
              </div>
            )}

            {/* Game instructions */}
            <p style={{ 
              marginTop: "15px", 
              fontSize: "12px", 
              color: "#ccc", 
              textAlign: "center",
              maxWidth: "300px"
            }}>
              Space/click/tap to flap  "A" to cast spell beam  Dodge obstacles/Dementors!
            </p>
          </div>

          {/* Right spacer */}
          <div style={{ flex: "1" }}></div>

          {/* Right side - Leaderboard */}
          <div style={{ 
            minWidth: "250px", 
            background: "rgba(0,0,0,0.7)", 
            padding: "20px", 
            borderRadius: "10px", 
            border: "2px solid #f3ce13",
            height: "fit-content"
          }}>
            <h3 style={{ 
              color: "#f3ce13", 
              margin: "0 0 15px 0", 
              textAlign: "center", 
              fontSize: "18px",
              textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
            }}>
               Leaderboard
            </h3>
            {leaderboard.length === 0 ? (
              <div style={{ 
                color: "#888", 
                textAlign: "center", 
                fontStyle: "italic",
                padding: "20px 0"
              }}>
                No scores yet
              </div>
            ) : (
              <ol style={{ 
                listStyle: "none", 
                padding: 0, 
                margin: 0 
              }}>
                {leaderboard.slice(0, 10).map((entry, i) => (
                  <li key={i} style={{ 
                    display: "flex", 
                    justifyContent: "space-between", 
                    alignItems: "center",
                    padding: "8px 12px",
                    margin: "5px 0",
                    background: i < 3 ? "rgba(243, 206, 19, 0.1)" : "rgba(255,255,255,0.05)",
                    borderRadius: "5px",
                    border: i < 3 ? "1px solid rgba(243, 206, 19, 0.3)" : "1px solid transparent"
                  }}>
                    <span style={{ 
                      fontWeight: "bold", 
                      color: i === 0 ? "#ffd700" : i === 1 ? "#c0c0c0" : i === 2 ? "#cd7f32" : "#fff",
                      fontSize: i < 3 ? "14px" : "13px"
                    }}>
                      {i + 1}. {entry.name}
                    </span>
                    <span style={{ 
                      color: "#f3ce13", 
                      fontWeight: "bold",
                      fontSize: i < 3 ? "14px" : "13px"
                    }}>
                      {entry.score}
                    </span>
                  </li>
                ))}
              </ol>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
