import React, { useRef, useEffect, useState } from "react";

const WIDTH = 400;
const HEIGHT = 600;
const PLAYER_W = 40;
const PLAYER_H = 40;
const OBSTACLE_W = 40;
const OBSTACLE_H = 40;
const API = "http://localhost:5000/api";
const GAME_DURATION_MS = 600000; // 10 minutes

type ScoreEntry = { name: string; score: number };

function getRandomX() {
    return Math.floor(Math.random() * (WIDTH - OBSTACLE_W));
}

function App() {
    const canvasRef = useRef<HTMLCanvasElement>(null);
    const [x, setX] = useState(WIDTH / 2 - PLAYER_W / 2);
    const [y, setY] = useState(HEIGHT - PLAYER_H);
    const [score, setScore] = useState(0);
    const [obstacles, setObstacles] = useState([{ x: getRandomX(), y: 0 }]);
    const [running, setRunning] = useState(true);
    const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
    const [name, setName] = useState("");
    const gameStartTimeRef = useRef<number>(Date.now());

    // Keyboard controls
    useEffect(() => {
        const handler = (e: KeyboardEvent) => {
            if (!running) return;
            if (e.key === "ArrowLeft") setX(x => Math.max(0, x - 24));
            if (e.key === "ArrowUp") setY(y => Math.max(0, y - 24));
            if (e.key === "ArrowDown") setY(y => Math.min(HEIGHT - PLAYER_H, y + 24));
            if (e.key === "ArrowRight") setX(x => Math.min(WIDTH - PLAYER_W, x + 24));
        };
        window.addEventListener("keydown", handler);
        return () => window.removeEventListener("keydown", handler);
    }, [running]);

    // Game loop
    useEffect(() => {
        if (!running) return;
        gameStartTimeRef.current = Date.now();
        const interval = setInterval(() => {
            setObstacles(obs => {
                let hit = false;
                // Move obstacles down and detect collision
                const next = obs.map(o => ({ ...o, y: o.y + 8 }));
                const survived = [];
                for (let o of next) {
                    // Only consider game over if bounding boxes overlap
                    const collided =
                        o.y + OBSTACLE_H > HEIGHT - PLAYER_H &&
                        o.y < HEIGHT &&
                        o.x < x + PLAYER_W &&
                        o.x + OBSTACLE_W > x;
                    if (collided) {
                        hit = true;
                    } else if (o.y < HEIGHT) {
                        survived.push(o); // Keep only uncollided, on-screen obstacles
                    }
                }

                if (hit) {
                    setRunning(false);
                    return obs;
                }

                // Fill up obstacles with a chance for new obstacles
                // You can tweak the frequency for difficulty/variety
                const spawnChance = Math.random();
                if (
                    survived.length === 0 || // always keep at least one block
                    (survived.length < 3 && spawnChance > 0.7)
                ) {
                    survived.push({ x: getRandomX(), y: 0 });
                }

                setScore(s => s + 1);
                return survived;
            });

            // Check for 10-minute limit
            if (Date.now() - gameStartTimeRef.current > GAME_DURATION_MS) {
                setRunning(false);
            }
        }, 36);

        return () => clearInterval(interval);
    }, [x, running]);

    // Draw
    useEffect(() => {
        const ctx = canvasRef.current?.getContext("2d");
        if (!ctx) return;
        ctx.clearRect(0, 0, WIDTH, HEIGHT);
        ctx.fillStyle = "blue";
        ctx.fillRect(x, HEIGHT - PLAYER_H, PLAYER_W, PLAYER_H);
        ctx.fillStyle = "red";
        obstacles.forEach(o => ctx.fillRect(o.x, o.y, OBSTACLE_W, OBSTACLE_H));
        ctx.fillStyle = "#222";
        ctx.fillText(`Score: ${score}`, 10, 24);

        if (!running) {
            ctx.fillStyle = "rgba(0,0,0,0.5)";
            ctx.fillRect(0, 0, WIDTH, HEIGHT);
            ctx.fillStyle = "#fff";
            ctx.font = "32px sans-serif";
            ctx.fillText("Game Over", WIDTH / 2 - 78, HEIGHT / 2);
        }
    });

    // Fetch leaderboard
    useEffect(() => {
        fetch(`${API}/leaderboard`)
            .then(res => res.json())
            .then(data => setLeaderboard(data));
    }, [running]);

    const restart = () => {
        setX(WIDTH / 2 - PLAYER_W / 2);
        setY(HEIGHT - PLAYER_H);
        setObstacles([{ x: getRandomX(), y: 0 }]);
        setScore(0);
        setRunning(true);
        gameStartTimeRef.current = Date.now();
    };

    const submitScore = () => {
        fetch(`${API}/submit`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: name || "Anonymous", score }),
        }).then(() => {
            fetch(`${API}/leaderboard`)
                .then(res => res.json())
                .then(data => setLeaderboard(data));
        });
    };

    return (
        <div style={{ textAlign: "center", fontFamily: "sans-serif" }}>
            <h2>Endless Runner Leaderboard</h2>
            <canvas ref={canvasRef} width={WIDTH} height={HEIGHT} style={{ border: "2px solid #888" }} />
            <div>
                Score: {score} {running ? null : <span>Game Over!</span>}
            </div>
            {!running && (
                <div>
                    <input placeholder="Your name" value={name} onChange={e => setName(e.target.value)} />
                    <button onClick={submitScore}>Submit Score</button>
                    <button onClick={restart}>Restart</button>
                </div>
            )}
            <h3>Leaderboard</h3>
            <ol>
                {leaderboard.map((entry, i) => (
                    <li key={i}>{entry.name}: {entry.score}</li>
                ))}
            </ol>
            <p>
                Use arrow keys to move & dodge red blocks!<br />
                Survive as long as you canâ€”game lasts up to 10 minutes or until a crash.<br />
                Submit your score and try again!
            </p>
        </div>
    );
}

export default App;
