import React from "react";

// Renders Harry Potter on a broomstick.
export const renderHarry = (x: number, y: number, PLAYER_W: number, PLAYER_H: number) => {
  const cx = x + PLAYER_W / 2, cy = y + PLAYER_H / 2;
  return (
    <g>
      {/* Broomstick */}
      <ellipse cx={cx} cy={y + PLAYER_H - 8} rx={PLAYER_W / 2 + 4} ry={3} fill="#8B4513" />
      {/* Head */}
      <circle cx={cx} cy={y + 18} r={18} fill="#F4C2A1" stroke="#D4A574" strokeWidth={2} />
      {/* Eyes/Glasses */}
      <ellipse cx={cx - 6} cy={y + 18} rx={7} ry={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none" />
      <ellipse cx={cx + 6} cy={y + 18} rx={7} ry={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none" />
      <circle cx={cx - 6} cy={y + 18} r={2} fill="#2C2C2C" />
      <circle cx={cx + 6} cy={y + 18} r={2} fill="#2C2C2C" />
      {/* Robe */}
      <rect x={x + 8} y={y + 36} width={PLAYER_W - 16} height={12} fill="#7F0000" rx={6}/>
      {/* Wand */}
      <rect x={x + PLAYER_W - 2} y={y + PLAYER_H / 2 - 1} width={20} height={2} fill="#8B4513" rx={1}/>
    </g>
  );
};

// Renders a lava column (pillar) as orange/red gradient
export const renderLavaColumn = (
  o: { x: number; height: number; id: number },
  OBSTACLE_W: number,
  OBSTACLE_GAP: number,
  GAME_HEIGHT: number
) => (
  <g key={o.id}>
    {/* Top lava */}
    <rect x={o.x} y={0} width={OBSTACLE_W} height={o.height}
      fill="url(#lavaGradient)" />
    {/* Bottom lava */}
    <rect x={o.x} y={o.height + OBSTACLE_GAP} width={OBSTACLE_W}
      height={GAME_HEIGHT - o.height - OBSTACLE_GAP}
      fill="url(#lavaGradient)" />
  </g>
);

// Lava gradient definition component (to insert once in SVG)
export const LavaDefs = () => (
  <defs>
    <linearGradient id="lavaGradient" x1="0" x2="0" y1="0" y2="1">
      <stop offset="0%" stopColor="#fd5" />
      <stop offset="40%" stopColor="#fd5" />
      <stop offset="70%" stopColor="#ff4500" />
      <stop offset="100%" stopColor="#b22222" />
    </linearGradient>
  </defs>
);

// Renders an enemy (random monster)
export const renderEnemy = (
  e: { x: number, y: number, color: string, type: string, id: number },
  ENEMY_W: number,
  ENEMY_H: number
) => (
  <g key={e.id}>
    <ellipse cx={e.x + ENEMY_W / 2} cy={e.y + ENEMY_H / 2} rx={ENEMY_W/2 - 2} ry={ENEMY_H/2 - 2} fill={e.color} stroke="black" strokeWidth={1.5} />
    <text x={e.x + ENEMY_W/2} y={e.y + ENEMY_H/2 + 4} fontSize="12" fill="#fff" textAnchor="middle">{e.type[0]}</text>
  </g>
);

// Renders all beam shots
export const renderBeams = (beams: { x: number, y: number, active: boolean }[], BEAM_W: number) =>
  beams.filter(b => b.active).map((b, i) =>
    <rect key={i} x={b.x} y={b.y} width={BEAM_W} height={6} fill="#ffd700" rx={3} />
  );
