export default function GameOverScreen({ name, setName, onSubmit, onMenu }) {
    return (
      <div>
        <div style={{ fontSize: "28px", color: "#c93845", marginBottom: 12 }}>Game Over!</div>
        <input
          style={{ margin: "8px 8px 8px 0", padding: "4px 8px", borderRadius: "4px", border: "1px solid #ccc", fontSize: "16px" }}
          placeholder="Your name"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <button onClick={onSubmit}
          style={{ marginLeft: "8px", fontSize: "16px", padding: "6px 18px", borderRadius: "4px", background: "#ffb400", fontWeight: "bold", letterSpacing: "1px", cursor: "pointer", border: "none" }}>
          Submit Score
        </button>
        <button onClick={onMenu}
          style={{ marginLeft: "10px", background: "#065f7b", color: "#fff", fontSize: "16px", padding: "6px 16px", borderRadius: "4px", fontWeight: "bold", letterSpacing: "0.5px", border: "none", cursor: "pointer" }}>
          Main Menu
        </button>
      </div>
    );
  }
