import React, { useState, useEffect, useRef } from "react";

const GAME_WIDTH = 400, GAME_HEIGHT = 600, BIRD_SIZE = 40, GRAVITY = 3, JUMP_HEIGHT = 60;
const PIPE_WIDTH = 50, PIPE_GAP = 170;
const API = "http://localhost:8000";

type Pipe = { x: number, height: number, id: number };
type ScoreEntry = { name: string; score: number };

function getRandomPipe(id: number) {
    const min = 50, max = GAME_HEIGHT - PIPE_GAP - 50;
    return { x: GAME_WIDTH, height: Math.floor(Math.random() * (max - min) + min), id };
}

function App() {
    const [birdY, setBirdY] = useState(GAME_HEIGHT / 2);
    const [velocity, setVelocity] = useState(0);
    const [pipes, setPipes] = useState([getRandomPipe(0)]);
    const [score, setScore] = useState(0);
    const [gameOver, setGameOver] = useState(false);
    const [running, setRunning] = useState(false);
    const [name, setName] = useState("");
    const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
    const pipeId = useRef(1);

    // Gravity and movement
    useEffect(() => {
        if (!running) return;
        const interval = setInterval(() => {
            setBirdY(y => Math.max(0, y + velocity));
            setVelocity(v => Math.min(v + GRAVITY, 12));
            setPipes(pipes => pipes.map(pipe => ({ ...pipe, x: pipe.x - 4 })));
        }, 24);
        return () => clearInterval(interval);
    }, [running, velocity]);

    // Jump on space/click/tap
    useEffect(() => {
        if (!running) return;
        const jump = () => setVelocity(-JUMP_HEIGHT);
        const onSpace = (e: KeyboardEvent) => { if (e.code === "Space") jump(); };
        window.addEventListener("keydown", onSpace);
        window.addEventListener("mousedown", jump);
        window.addEventListener("touchstart", jump);
        return () => {
            window.removeEventListener("keydown", onSpace);
            window.removeEventListener("mousedown", jump);
            window.removeEventListener("touchstart", jump);
        };
    }, [running]);

    // Generate new pipes and scoring/collision
    useEffect(() => {
        if (!running) return;
        // Remove passed pipes and add new ones
        setPipes(pipes => {
            if (pipes.length === 0 || pipes[pipes.length - 1].x < GAME_WIDTH - 200) {
                pipes = [...pipes, getRandomPipe(pipeId.current++)];
            }
            return pipes.filter(pipe => pipe.x + PIPE_WIDTH > 0);
        });

        // Collision and scoring
        pipes.forEach(pipe => {
            // Top and bottom of pipe
            if (
                (pipe.x < 60 && pipe.x + PIPE_WIDTH > 20) &&
                (birdY < pipe.height || birdY + BIRD_SIZE > pipe.height + PIPE_GAP)
            ) {
                setGameOver(true);
                setRunning(false);
            }
            if (pipe.x + PIPE_WIDTH === 20) setScore(s => s + 1);
        });
        // Ground/ceiling collision
        if (birdY >= GAME_HEIGHT - BIRD_SIZE || birdY <= 0) {
            setGameOver(true);
            setRunning(false);
        }
    }, [birdY, pipes, running]);

    // Fetch leaderboard
    useEffect(() => {
        fetch(`${API}/leaderboard`)
            .then(res => res.json())
            .then(setLeaderboard);
    }, [gameOver]);

    // Reset game
    function startGame() {
        setBirdY(GAME_HEIGHT / 2);
        setVelocity(0);
        setPipes([getRandomPipe(0)]);
        setScore(0);
        setGameOver(false);
        setRunning(true);
        pipeId.current = 1;
    }

    // Submit new score
    function submitScore() {
        fetch(`${API}/submit`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: name || "Anonymous", score }),
        })
            .then(() => fetch(`${API}/leaderboard`))
            .then(res => res.json())
            .then(setLeaderboard);
    }

    // Render
    return (
        <div style={{ textAlign: "center" }}>
            <h2>Flappy Bird Leaderboard</h2>
            <div style={{
                position: 'relative', margin: "20px auto", width: GAME_WIDTH, height: GAME_HEIGHT,
                background: "#87ceeb", border: "2px solid #444", overflow: "hidden"
            }}>
                {/* Bird */}
                <div style={{
                    position: "absolute",
                    left: 20, top: birdY,
                    width: BIRD_SIZE, height: BIRD_SIZE,
                    background: "yellow", borderRadius: "50%", border: "2px solid #111"
                }} />
                {/* Pipes */}
                {pipes.map(pipe => (
                    <React.Fragment key={pipe.id}>
                        {/* Upper pipe */}
                        <div style={{
                            position: "absolute", left: pipe.x, top: 0,
                            width: PIPE_WIDTH, height: pipe.height,
                            background: "green", border: "3px solid #0a0"
                        }} />
                        {/* Lower pipe */}
                        <div style={{
                            position: "absolute", left: pipe.x, top: pipe.height + PIPE_GAP,
                            width: PIPE_WIDTH, height: GAME_HEIGHT - pipe.height - PIPE_GAP,
                            background: "green", border: "3px solid #0a0"
                        }} />
                    </React.Fragment>
                ))}
            </div>
            <div>Score: {score}</div>
            {!running && (gameOver ? (
                <div>
                    <b>Game Over!</b><br />
                    <input placeholder="Your name" value={name} onChange={e => setName(e.target.value)} />
                    <button onClick={submitScore}>Submit Score</button>
                    <button onClick={startGame}>Restart</button>
                </div>
            ) : (
                <button onClick={startGame}>Start Game</button>
            ))}
            <h3>Leaderboard</h3>
            <ol>
                {leaderboard.map((entry, i) => (
                    <li key={i}>{entry.name}: {entry.score}</li>
                ))}
            </ol>
            <p>
                Tap, click, or press space to flap! Avoid the pipes.<br />
                Submit your best score.
            </p>
        </div>
    );
}

export default App;
