import React, { useRef, useEffect, useState } from "react";
import { renderHarry, renderLavaColumn, LavaDefs, renderEnemy, renderBeams } from "./GameEntities";

const GAME_WIDTH = 400, GAME_HEIGHT = 600, PLAYER_W = 48, PLAYER_H = 48;
const OBSTACLE_W = 60, OBSTACLE_GAP = 160, ENEMY_W = 40, ENEMY_H = 40, BEAM_W = 24;
const OBSTACLE_SPEED = 4, ENEMY_SPEED = 5;
const MOVE_AMOUNT = 20;
const PLAYER_START_X = 60, PLAYER_START_Y = GAME_HEIGHT / 2;
const API = "http://localhost:8000";
const ENEMY_COLORS = ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"];
const ENEMY_NAMES = ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"];

type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

function getRandomObstacle(id: number): Obstacle {
  const min = 50, max = GAME_HEIGHT - OBSTACLE_GAP - 50;
  return { x: GAME_WIDTH, height: Math.floor(Math.random() * (max - min) + min), id };
}
function getRandomEnemy(id: number): Enemy {
  const color = ENEMY_COLORS[Math.floor(Math.random() * ENEMY_COLORS.length)];
  const type = ENEMY_NAMES[Math.floor(Math.random() * ENEMY_NAMES.length)];
  const y = Math.floor(Math.random() * (GAME_HEIGHT - ENEMY_H - 20)) + 10;
  return { x: GAME_WIDTH, y, color, type, id };
}

export default function App() {
  const [x, setX] = useState(PLAYER_START_X);
  const [y, setY] = useState(PLAYER_START_Y);
  const [obstacles, setObstacles] = useState([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
  const [score, setScore] = useState(0);
  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
  const obstacleId = useRef(1); const enemyId = useRef(1);

  // Main game loop
  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      setScore(s => s + 1);

      // Move lava columns
      setObstacles(obs => {
        let nobs = obs.map(o => ({ ...o, x: o.x - OBSTACLE_SPEED }));
        if (nobs.length === 0 || nobs[nobs.length - 1].x < GAME_WIDTH - 200) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        return nobs.filter(o => o.x + OBSTACLE_W > 0);
      });

      setEnemies(enms => {
        let nenms = enms.map(e => ({ ...e, x: e.x - ENEMY_SPEED }));
        if (Math.random() > 0.97) nenms.push(getRandomEnemy(enemyId.current++));
        return nenms.filter(e => e.x + ENEMY_W > 0);
      });

      setBeams(bs =>
        bs.filter(b => b.active && b.x < GAME_WIDTH).map(b => ({ ...b, x: b.x + 14 }))
      );
    }, 24);
    return () => clearInterval(interval);
  }, [running]);

  // All arrow key movement and shooting
  useEffect(() => {
    if (!running) return;
    const handleKey = (e: KeyboardEvent) => {
      if (e.key === "ArrowLeft") setX(x => Math.max(0, x - MOVE_AMOUNT));
      if (e.key === "ArrowRight") setX(x => Math.min(GAME_WIDTH - PLAYER_W, x + MOVE_AMOUNT));
      if (e.key === "ArrowUp") setY(y => Math.max(0, y - MOVE_AMOUNT));
      if (e.key === "ArrowDown") setY(y => Math.min(GAME_HEIGHT - PLAYER_H, y + MOVE_AMOUNT));
      if (e.key === "a" || e.key === "A") setBeams(bs => [...bs, { x: x + PLAYER_W, y: y + PLAYER_H / 2 - 4, active: true }]);
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [x, y, running]);

  // Collision logic
  useEffect(() => {
    if (!running) return;

    // Lava collision
    const harryBox = { left: x, right: x + PLAYER_W, top: y, bottom: y + PLAYER_H };
    for (const obs of obstacles) {
      // Only check at Harry X overlap
      if (
        obs.x < harryBox.right &&
        obs.x + OBSTACLE_W > harryBox.left &&
        (harryBox.top < obs.height || harryBox.bottom > obs.height + OBSTACLE_GAP)
      ) {
        setRunning(false); setGameOver(true); return;
      }
    }

    for (const enemy of enemies) {
      if (
        harryBox.left < enemy.x + ENEMY_W && harryBox.right > enemy.x &&
        harryBox.top < enemy.y + ENEMY_H && harryBox.bottom > enemy.y
      ) {
        setRunning(false); setGameOver(true); return;
      }
    }

     setBeams(bs => {
       const newBeams = [...bs];
       setEnemies(enms => enms.filter(enemy => {
         let hit = false;
         for (let i = 0; i < newBeams.length; i++) {
           const beam = newBeams[i];
           if (
             beam.active &&
             beam.y > enemy.y && beam.y < enemy.y + ENEMY_H &&
             beam.x + BEAM_W > enemy.x && beam.x < enemy.x + ENEMY_W
           ) {
             hit = true; 
             newBeams[i] = { ...beam, active: false }; 
             break;
           }
         }
         return !hit;
       }));
       return newBeams;
     });

    // Out of bounds
    if (x < 0 || x + PLAYER_W > GAME_WIDTH || y < 0 || y + PLAYER_H > GAME_HEIGHT) {
      setRunning(false); setGameOver(true); return;
    }
  }, [beams, x, y, obstacles, enemies, running]);

  useEffect(() => {
    fetch(`${API}/leaderboard`)
      .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => {});
  }, [gameOver]);

  function startGame() {
    setX(PLAYER_START_X); setY(PLAYER_START_Y);
    setObstacles([getRandomObstacle(0)]); setEnemies([]); setBeams([]);
    setScore(0); setGameOver(false); setRunning(true);
    obstacleId.current = 1; enemyId.current = 1;
  }
  function submitScore() {
    fetch(`${API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    })
      .then(() => fetch(`${API}/leaderboard`))
      .then(res => res.json())
      .then(setLeaderboard);
  }

  return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      <h2 style={{ color: "#f3ce13", padding: "16px 0", fontFamily: "Harry Potter, serif" }}>
        Harry Potter: Lava Cave Run!
      </h2>
      <svg width={GAME_WIDTH} height={GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51" }}>
        <LavaDefs />
        {[...Array(20)].map((_, i) => (
          <circle key={i} cx={Math.random()*GAME_WIDTH} cy={Math.random()*GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
        ))}
        {obstacles.map(o => renderLavaColumn(o, OBSTACLE_W, OBSTACLE_GAP, GAME_HEIGHT))}
        {enemies.map(e => renderEnemy(e, ENEMY_W, ENEMY_H))}
        {renderBeams(beams, BEAM_W)}
        {renderHarry(x, y, PLAYER_W, PLAYER_H)}
      </svg>
      <div>Score: {score}</div>
      {!running && (gameOver ? (
        <div>
          <b>Game Over!</b><br />
          <input style={{ margin: 4 }} placeholder="Your name" value={name} onChange={e => setName(e.target.value)} />
          <button onClick={submitScore}>Submit Score</button>
          <button onClick={startGame}>Restart</button>
        </div>
      ) : (
        <button onClick={startGame}>Start Game</button>
      ))}
      <h3>Leaderboard</h3>
      <ol>{leaderboard.map((entry, i) => (
        <li key={i}>{entry.name}: {entry.score}</li>
      ))}</ol>
      <p>Arrow keys: free movement · "A": shoot wand · Dodge the lava!</p>
    </div>
  );
}
