import React from "react";

export const LavaDefs = () => (
  <defs>
    <linearGradient id="lavaGradient" x1="0" x2="0" y1="0" y2="1">
      <stop offset="0%" stopColor="#fffc97" />
      <stop offset="45%" stopColor="#fd5" />
      <stop offset="65%" stopColor="#ff4500" />
      <stop offset="100%" stopColor="#7a1307" />
    </linearGradient>
    <filter id="lavaGlow" x="-30%" y="-30%" width="160%" height="160%">
      <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
      <feMerge>
        <feMergeNode in="coloredBlur"/>
        <feMergeNode in="SourceGraphic"/>
      </feMerge>
    </filter>
  </defs>
);

function wavyPath(x: number, y: number, width: number, height: number, flip=false) {
  let points = [];
  const segments = 6;
  for (let i = 0; i <= segments; i++) {
    const ratio = i / segments;
    const px = x + (flip ? width : 0);
    const py = y + height * ratio + Math.sin(ratio * Math.PI * 2 + (flip ? 1 : 0)) * 13 * (ratio!==0&&ratio!==1?1:0);
    points.push([px, py]);
  }
  return points.map((p,i) => (i===0?"M":"L") + p[0] + "," + p[1]).join(" ");
}

export const renderLavaColumn = (
  o: { x: number; height: number; id: number },
  OBSTACLE_W: number,
  OBSTACLE_GAP: number,
  GAME_HEIGHT: number
) => {
  const topH = o.height, gap = OBSTACLE_GAP, botY = o.height + gap;
  const x = o.x, w = OBSTACLE_W;
  const topLeft = wavyPath(x, 0, w, topH, false);
  const topRight = wavyPath(x + w, 0, -w, topH, true).replace(/^M/,"L");
  const topPath = `${topLeft}${topRight}Z`;
  const botLeft = wavyPath(x, botY, w, GAME_HEIGHT - botY, false);
  const botRight = wavyPath(x + w, botY, -w, GAME_HEIGHT - botY, true).replace(/^M/,"L");
  const botPath = `${botLeft}${botRight}Z`;
  return (
    <g key={o.id}>
      <path d={topPath} fill="url(#lavaGradient)" filter="url(#lavaGlow)" stroke="#ff5522" strokeWidth={2} />
      <path d={botPath} fill="url(#lavaGradient)" filter="url(#lavaGlow)" stroke="#ff5522" strokeWidth={2} />
    </g>
  );
};

export const renderHarry = (
  x: number, y: number, PLAYER_W: number, PLAYER_H: number, invincible: boolean
) => {
  const cx = x + PLAYER_W / 2;
  return (
    <g>
      {invincible && (
        <circle
          cx={cx}
          cy={y + 24}
          r={27}
          fill="#fa0"
          opacity={0.32}
          style={{filter: "blur(1.5px)"}}
        />
      )}
      {/* Broomstick */}
      <ellipse cx={cx} cy={y + PLAYER_H - 8} rx={PLAYER_W / 2 + 4} ry={3} fill="#8B4513" />
      {/* Head */}
      <circle cx={cx} cy={y + 18} r={18} fill="#F4C2A1" stroke="#D4A574" strokeWidth={2} />
      <ellipse cx={cx - 6} cy={y + 18} rx={7} ry={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none" />
      <ellipse cx={cx + 6} cy={y + 18} rx={7} ry={7} stroke="#2C2C2C" strokeWidth={2.5} fill="none" />
      <circle cx={cx - 6} cy={y + 18} r={2} fill="#2C2C2C" />
      <circle cx={cx + 6} cy={y + 18} r={2} fill="#2C2C2C" />
      {/* Robe */}
      <rect x={x + 8} y={y + 36} width={PLAYER_W - 16} height={12} fill="#7F0000" rx={6}/>
      {/* Wand */}
      <rect x={x + PLAYER_W - 2} y={y + PLAYER_H / 2 - 1} width={20} height={2} fill="#8B4513" rx={1}/>
    </g>
  );
};

export const renderEnemy = (e: { x: number, y: number, color: string, type: string, id: number }, ENEMY_W: number, ENEMY_H: number) => (
  <g key={e.id}>
    <ellipse cx={e.x + ENEMY_W / 2} cy={e.y + ENEMY_H / 2} rx={ENEMY_W/2 - 2} ry={ENEMY_H/2 - 2} fill={e.color} stroke="black" strokeWidth={1.5} />
    <text x={e.x + ENEMY_W/2} y={e.y + ENEMY_H/2 + 4} fontSize="12" fill="#fff" textAnchor="middle">{e.type[0]}</text>
  </g>
);

export const renderPotion = (p: { x: number, y: number, id: number }, POTION_W: number, POTION_H: number) => (
  <g key={p.id}>
    <ellipse cx={p.x + POTION_W/2} cy={p.y + POTION_H/2} rx={POTION_W/2} ry={POTION_H/2} fill="#8cf" stroke="#44a" strokeWidth={2}/>
    <ellipse cx={p.x + POTION_W/2} cy={p.y + 6} rx={8} ry={4} fill="#fff" opacity={0.7}/>
    <rect x={p.x + POTION_W/2 - 5} y={p.y - 6} width={10} height={7} fill="#a75" rx={2}/>
    <circle cx={p.x + POTION_W/2} cy={p.y + POTION_H/2 + 3} r={5} fill="#35fa8b" opacity={0.65}/>
    <circle cx={p.x + POTION_W/2 + 8} cy={p.y + POTION_H/2 - 6} r={2} fill="#fff" opacity={0.6}/>
  </g>
);

export const renderBeams = (beams: { x: number, y: number, active: boolean }[], BEAM_W: number) =>
  beams.filter(b => b.active).map((b, i) =>
    <rect key={i} x={b.x} y={b.y} width={BEAM_W} height={6} fill="#ffd700" rx={3} />
  );
