import React from 'react';

interface HelpModalProps {
  onClose: () => void;
}

export default function HelpModal({ onClose }: HelpModalProps) {
  return (
    <div style={{
      position: "fixed", left: 0, top: 0, width: "100%", height: "100%", zIndex: 100,
      background: "rgba(0,0,0,0.85)", display: "flex", alignItems: "center", justifyContent: "center"
    }}>
      <div style={{
        minWidth: "450px", maxWidth: "90vw", 
        background: "linear-gradient(135deg, #1A0E3D, #2D1B69, #1C1C1C)", 
        padding: "40px", borderRadius: "20px", border: "3px solid #D4AF37",
        boxShadow: "0 12px 40px rgba(0,0,0,0.6), inset 0 1px 0 rgba(212, 175, 55, 0.2)", 
        backdropFilter: "blur(15px)",
        color: "#C0C0C0", lineHeight: 1.6, fontSize: 16
      }}>
        <div style={{ 
          fontSize: "28px", color: "#D4AF37", marginBottom: "25px", textAlign: "center",
          textShadow: "3px 3px 6px rgba(0,0,0,0.8), 0 0 15px rgba(212, 175, 55, 0.4)", 
          fontWeight: "bold",
          letterSpacing: "1px"
        }}>
          ðŸ“– How to Play
        </div>
        <div style={{ marginBottom: "25px" }}>
          <div style={{ 
            marginBottom: "15px", fontSize: "20px", color: "#D4AF37", fontWeight: "bold",
            textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
          }}>Controls:</div>
          <ul style={{ margin: "0 0 25px 0", paddingLeft: "25px" }}>
            <li style={{ marginBottom: "10px", color: "#C0C0C0" }}>
              <b style={{ color: "#D4AF37" }}>Click / Tap / Up Arrow</b> - Flap Harry upward
            </li>
            <li style={{ marginBottom: "10px", color: "#C0C0C0" }}>
              <b style={{ color: "#D4AF37" }}>Space Bar</b> - Shoot a magic beam straight right
            </li>
            <li style={{ marginBottom: "10px", color: "#C0C0C0" }}>
              <b style={{ color: "#D4AF37" }}>P Key</b> - Pause/Resume game
            </li>
          </ul>
          <div style={{ 
            marginBottom: "15px", fontSize: "20px", color: "#D4AF37", fontWeight: "bold",
            textShadow: "1px 1px 3px rgba(0,0,0,0.8)"
          }}>Goal:</div>
          <p style={{ 
            margin: "0 0 25px 0", color: "#C0C0C0",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)"
          }}>
            Fly as far as you can! Avoid obstacles and defeat Witches (dark enemies) using your magic beam.<br />
            Score points by surviving longer and zapping more Witches!
          </p>
        </div>
        <button
          onClick={onClose}
          style={{
            width: "100%", padding: "16px 32px", 
            background: "linear-gradient(135deg, #0F4C3C, #1E3A8A)",
            color: "#D4AF37", border: "2px solid #D4AF37", borderRadius: "12px", 
            fontWeight: "bold", cursor: "pointer", 
            fontSize: "18px", 
            boxShadow: "0 6px 20px rgba(0,0,0,0.4), inset 0 1px 0 rgba(212, 175, 55, 0.3)", 
            textTransform: "uppercase", letterSpacing: "2px",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)",
            transition: "all 0.3s ease"
          }}>Close</button>
      </div>
    </div>
  );
}
