import React from 'react';

interface GameOverScreenProps {
  score: number;
  name: string;
  setName: (name: string) => void;
  onSubmit: () => void;
  onPlayAgain: () => void;
  onMainMenu: () => void;
}

export default function GameOverScreen({ score, name, setName, onSubmit, onPlayAgain, onMainMenu }: GameOverScreenProps) {
  return (
    <div style={{
      width: 800, height: 600, 
      background: "linear-gradient(135deg, #1A0E3D, #2D1B69, #1C1C1C)", 
      border: "4px solid #D4AF37", borderRadius: "12px",
      display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center",
      textAlign: "center", padding: "30px", 
      boxShadow: "0 12px 40px rgba(0,0,0,0.6), inset 0 1px 0 rgba(212, 175, 55, 0.2)"
    }}>
      <h2 style={{ 
        color: "#8B0000", 
        margin: "0 0 25px 0", 
        fontSize: "36px", 
        textShadow: "3px 3px 6px rgba(0,0,0,0.8), 0 0 15px rgba(139, 0, 0, 0.4)",
        letterSpacing: "2px",
        fontFamily: "Harry Potter, serif"
      }}>
        Game Over!
      </h2>
      <div style={{ 
        fontSize: "24px", 
        color: "#D4AF37", 
        marginBottom: "30px", 
        fontWeight: "bold",
        textShadow: "2px 2px 4px rgba(0,0,0,0.8), 0 0 10px rgba(212, 175, 55, 0.3)"
      }}>
        Final Score: {score}
      </div>
      <div style={{ marginBottom: "25px" }}>
        <input
          style={{
            padding: "12px 18px", 
            borderRadius: "8px", 
            border: "2px solid #D4AF37",
            background: "linear-gradient(135deg, #2D1B69, #1A0E3D)", 
            color: "#D4AF37", 
            fontSize: "16px", 
            width: "250px", 
            textAlign: "center",
            boxShadow: "inset 0 2px 4px rgba(0,0,0,0.3)",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)"
          }}
          placeholder="Enter your name"
          value={name}
          onChange={e => setName(e.target.value)}
        />
      </div>
      <div style={{ display: "flex", flexDirection: "column", gap: "12px", width: "100%" }}>
        <button onClick={onSubmit}
          style={{
            padding: "14px 28px", 
            background: "linear-gradient(135deg, #0F4C3C, #1E3A8A)",
            color: "#D4AF37", 
            border: "2px solid #D4AF37", 
            borderRadius: "10px", 
            fontWeight: "bold", 
            cursor: "pointer",
            fontSize: "16px", 
            boxShadow: "0 6px 20px rgba(0,0,0,0.4), inset 0 1px 0 rgba(212, 175, 55, 0.3)", 
            textTransform: "uppercase", 
            letterSpacing: "1px",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)",
            transition: "all 0.3s ease"
          }}>
          Submit Score
        </button>
        <button onClick={onPlayAgain}
          style={{
            padding: "14px 28px", 
            background: "linear-gradient(135deg, #2D1B69, #1A0E3D)",
            color: "#D4AF37", 
            border: "2px solid #D4AF37", 
            borderRadius: "10px", 
            fontWeight: "bold", 
            cursor: "pointer",
            fontSize: "16px", 
            boxShadow: "0 6px 20px rgba(0,0,0,0.4), inset 0 1px 0 rgba(212, 175, 55, 0.3)", 
            textTransform: "uppercase", 
            letterSpacing: "1px",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)",
            transition: "all 0.3s ease"
          }}>
          Play Again
        </button>
        <button onClick={onMainMenu}
          style={{
            padding: "14px 28px", 
            background: "linear-gradient(135deg, #1A0E3D, #2D1B69)",
            color: "#D4AF37", 
            border: "2px solid #D4AF37", 
            borderRadius: "10px", 
            fontWeight: "bold", 
            cursor: "pointer",
            fontSize: "16px", 
            boxShadow: "0 6px 20px rgba(0,0,0,0.4), inset 0 1px 0 rgba(212, 175, 55, 0.3)", 
            textTransform: "uppercase", 
            letterSpacing: "1px",
            textShadow: "1px 1px 2px rgba(0,0,0,0.8)",
            transition: "all 0.3s ease"
          }}>
          Main Menu
        </button>
      </div>
    </div>
  );
}
