import React, { useRef, useEffect, useState } from "react";

const GAME_WIDTH = 400;
const GAME_HEIGHT = 600;
const PLAYER_W = 48;
const PLAYER_H = 48;
const GRAVITY = 0.6;
const FLAP_VEL = -8;
const OBSTACLE_W = 60;
const OBSTACLE_GAP = 180;
const ENEMY_W = 40;
const ENEMY_H = 40;
const BEAM_W = 10;
const API = "http://localhost:8000";

const ENEMY_COLORS = ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"];
const ENEMY_NAMES = ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"];

type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

function getRandomObstacle(id: number): Obstacle {
  const min = 50, max = GAME_HEIGHT - OBSTACLE_GAP - 50;
  return { x: GAME_WIDTH, height: Math.floor(Math.random() * (max - min) + min), id };
}

function getRandomEnemy(id: number): Enemy {
  const color = ENEMY_COLORS[Math.floor(Math.random() * ENEMY_COLORS.length)];
  const type = ENEMY_NAMES[Math.floor(Math.random() * ENEMY_NAMES.length)];
  const y = Math.floor(Math.random() * (GAME_HEIGHT - ENEMY_H - 20)) + 10;
  return { x: GAME_WIDTH, y, color, type, id };
}

export default function App() {
  const [playerY, setPlayerY] = useState(GAME_HEIGHT / 2);
  const [vel, setVel] = useState(0);
  const [obstacles, setObstacles] = useState([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
  const [score, setScore] = useState(0);
  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
  const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
  const obstacleId = useRef(1);
  const enemyId = useRef(1);

  // Main loop
  useEffect(() => {
    if (!running) return;
    const interval = setInterval(() => {
      setVel(v => Math.min(v + GRAVITY, 10));
      setPlayerY(y => Math.max(0, Math.min(GAME_HEIGHT - PLAYER_H, y + vel)));
      setScore(s => s + 1);

      // Obstacles move left
      setObstacles(obs => {
        let nobs = obs.map(o => ({ ...o, x: o.x - 4 }));
        if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < GAME_WIDTH - 200)) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        return nobs.filter(o => o.x + OBSTACLE_W > 0);
      });

      // Enemies move left
      setEnemies(enms => {
        let nenms = enms.map(e => ({ ...e, x: e.x - 5 }));
        // Spawn random enemies
        if (Math.random() > 0.97) {
          nenms.push(getRandomEnemy(enemyId.current++));
        }
        return nenms.filter(e => e.x + ENEMY_W > 0);
      });

      // Beam movement and hit detection
      setBeams(bs =>
        bs
          .filter(b => b.active && b.x < GAME_WIDTH)
          .map(b => ({ ...b, x: b.x + 12 }))
      );

    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  // Flap/beam controls
  useEffect(() => {
    if (!running) return;

    const flap = () => setVel(FLAP_VEL);
    const cast = () => {
      setBeams(bs => [
        ...bs,
        { x: 30 + PLAYER_W, y: playerY + PLAYER_H / 2 - 4, active: true }
      ]);
    };

    const onKey = (e: KeyboardEvent) => {
      if (e.code === "Space") flap();
      if (e.key === "a" || e.key === "A") cast();
      if (e.key === "ArrowUp") setVel(FLAP_VEL);
    };
    window.addEventListener("keydown", onKey);
    window.addEventListener("mousedown", flap);
    window.addEventListener("touchstart", flap);
    return () => {
      window.removeEventListener("keydown", onKey);
      window.removeEventListener("mousedown", flap);
      window.removeEventListener("touchstart", flap);
    };
  }, [playerY, running]);

  // Collision, enemy/beam logic, and game over
  useEffect(() => {
    if (!running) return;

    // Collisions with obstacles
    const harryTop = playerY;
    const harryBot = playerY + PLAYER_H;
    const harryLeft = 30;
    const harryRight = 30 + PLAYER_W;

    // Flappy barrier collision: only check at Harry's X
    for (const obs of obstacles) {
      if (
        obs.x < harryRight &&
        obs.x + OBSTACLE_W > harryLeft &&
        (harryTop < obs.height || harryBot > obs.height + OBSTACLE_GAP)
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }

    // Collisions with any enemy
    for (const enemy of enemies) {
      if (
        enemy.x < harryRight &&
        enemy.x + ENEMY_W > harryLeft &&
        enemy.y < harryBot &&
        enemy.y + ENEMY_H > harryTop
      ) {
        setRunning(false);
        setGameOver(true);
      }
    }

    // Beam to enemy collision
    setEnemies(enms => enms.filter(enemy => {
      let hit = false;
      setBeams(bs =>
        bs.map(b => {
          if (
            b.active &&
            b.y > enemy.y && b.y < enemy.y + ENEMY_H &&
            b.x + BEAM_W > enemy.x && b.x < enemy.x + ENEMY_W
          ) {
            hit = true;
            return { ...b, active: false };
          }
          return b;
        })
      );
      return !hit;
    }));

    // Out of bounds
    if (playerY <= 0 || playerY + PLAYER_H >= GAME_HEIGHT) {
      setRunning(false);
      setGameOver(true);
    }
    // eslint-disable-next-line
  }, [playerY, obstacles, enemies, beams, running]);

  // Leaderboard fetch
  useEffect(() => {
    fetch(`${API}/leaderboard`)
      .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => {});
  }, [gameOver]);

  function startGame() {
    setPlayerY(GAME_HEIGHT / 2);
    setVel(0);
    setObstacles([getRandomObstacle(0)]);
    setEnemies([]);
    setBeams([]);
    setScore(0);
    setGameOver(false);
    setRunning(true);
    obstacleId.current = 1;
    enemyId.current = 1;
  }

  function submitScore() {
    fetch(`${API}/submit`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: name || "HarryPotterFan", score }),
    })
      .then(() => fetch(`${API}/leaderboard`))
      .then(res => res.json())
      .then(setLeaderboard);
  }

  // Drawing Harry (broomstick and wand effect, very basic)
  function renderHarry() {
    return (
      <g>
        {/* Broomstick (brown line under Harry) */}
        <rect x={30} y={playerY + PLAYER_H - 12} width={PLAYER_W} height={6} fill="#985211" rx={3}/>
        {/* Harry (circle face, scarf, glasses, bolt) */}
        <circle cx={30 + PLAYER_W/2} cy={playerY + 20} r={16} fill="#ffe0bd" stroke="#333" strokeWidth={2}/>
        {/* Glasses */}
        <ellipse cx={30 + PLAYER_W/2 - 7} cy={playerY + 20} rx={6} ry={6} stroke="#222" strokeWidth={2} fill="none"/>
        <ellipse cx={30 + PLAYER_W/2 + 7} cy={playerY + 20} rx={6} ry={6} stroke="#222" strokeWidth={2} fill="none"/>
        <rect x={30 + PLAYER_W/2 - 2} y={playerY + 20} width={4} height={2} fill="#222"/>{/* bridge */}
        {/* Scar */}
        <polyline points={`${30 + PLAYER_W/2 - 3},${playerY + 9} ${30 + PLAYER_W/2},${playerY + 14} ${30 + PLAYER_W/2 + 3},${playerY + 11}`} stroke="gold" strokeWidth={2} fill="none"/>
        {/* Body/Hoodie */}
        <rect x={30 + 10} y={playerY + 36} width={PLAYER_W-20} height={12} fill="#222345" rx={5}/>
        {/* Wand (flash when shooting) */}
        <rect x={30 + PLAYER_W - 4} y={playerY + PLAYER_H/2 - 2} width={16} height={4} fill="#49311c" rx={2}/>
      </g>
    );
  }

  // Drawing Enemies
  function renderEnemy(e: Enemy) {
    return (
      <g>
        <rect
          x={e.x} y={e.y}
          width={ENEMY_W} height={ENEMY_H}
          fill={e.color}
          rx={8}
          stroke="black"
        />
        {/* Monster type initials */}
        <text x={e.x + ENEMY_W / 2} y={e.y + ENEMY_H / 2 + 6} fontSize="16" fill="#fff" textAnchor="middle">
          {e.type[0]}
        </text>
      </g>
    );
  }

  // Drawing Obstacles (magic barriers/trees/etc)
  function renderObstacle(o: Obstacle) {
    return (
      <g key={o.id}>
        {/* Top barrier */}
        <rect x={o.x} y={0} width={OBSTACLE_W} height={o.height} fill="#b8c2ff" />
        {/* Bottom barrier */}
        <rect x={o.x} y={o.height + OBSTACLE_GAP} width={OBSTACLE_W} height={GAME_HEIGHT - o.height - OBSTACLE_GAP} fill="#b8c2ff" />
        {/* Spark upgrades: add potion, star, etc here for bonus later */}
      </g>
    );
  }

  // Drawing Beams
  function renderBeams() {
    return beams.filter(b => b.active).map((b, i) =>
      <rect key={i} x={b.x} y={b.y} width={24} height={6} fill="#ffd700" rx={3} />
    );
  }

  return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif" }}>
        Harry Potter: Broomstick Blitz!
      </h2>
      <svg width={GAME_WIDTH} height={GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51" }}>
        {/* Starry sky */}
        {[...Array(24)].map((_, i) => (
          <circle key={i} cx={Math.random()*GAME_WIDTH} cy={Math.random()*GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
        ))}
        {/* Obstacles */}
        {obstacles.map(renderObstacle)}
        {/* Enemies */}
        {enemies.map(renderEnemy)}
        {/* Beams */}
        {renderBeams()}
        {/* Harry */}
        {renderHarry()}
      </svg>
      <div style={{ fontSize: 22, margin: "8px 0" }}>
        Score: {score}
      </div>
      {!running && (gameOver ? (
        <div>
          <b style={{ fontSize: 28, color: "#edd13b" }}>Game Over!</b><br />
          <input
            style={{ margin: 4, fontSize: 18, padding: 4, borderRadius: 5 }}
            placeholder="Your name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <button style={{ fontSize: 18 }} onClick={submitScore}>Submit Score</button>
          <button style={{ fontSize: 18, marginLeft: 8 }} onClick={startGame}>Restart</button>
        </div>
      ) : (
        <button style={{ fontSize: 22, marginTop: 8 }} onClick={startGame}>Start Game</button>
      ))}
      <h3 style={{ color: "#ffb400" }}>Leaderboard</h3>
      <ol style={{ maxWidth: 300, margin: "0 auto", background: "rgba(255,255,255,0.15)", borderRadius: 8, fontSize: 20 }}>
        {leaderboard.map((entry, i) => (
          <li key={i}>
            <span style={{color: "#f1ee04"}}>{entry.name}</span>: {entry.score}
          </li>
        ))}
      </ol>
      <p style={{ marginTop: 12, fontSize: 18 }}>
        Controls: <b>Space/Click/Tap</b> to fly Â· <b>A</b> to cast spell<br/>
        Dodge barriers and monsters, zap enemies with your wand!
      </p>
    </div>
  );
}
