import React, { useRef, useEffect, useState } from "react";
import { renderHarry, renderObstacle, renderEnemy, renderBeams } from "./GameEntities";

// ----- GAME CONFIGURATION AND CONSTANTS -----
const CONFIG = {
  GAME_WIDTH: 800,
  GAME_HEIGHT: 600,
  PLAYER_W: 48,
  PLAYER_H: 48,
  GRAVITY: 0.6,
  FLAP_VEL: -8,
  OBSTACLE_W: 60,
  OBSTACLE_GAP: 220,
  ENEMY_W: 40,
  ENEMY_H: 40,
  BEAM_W: 10,
  API: "http://localhost:8000",
  ENEMY_COLORS: ["#d72631", "#006e90", "#ffb400", "#8f3985", "#4ca1af"],
  ENEMY_NAMES: ["Dementor", "Death Eater", "Troll", "Acromantula", "Werewolf"],
};

// ----- TYPE DEFINITIONS -----
type Obstacle = { x: number; height: number; id: number };
type Enemy = { x: number; y: number; color: string; type: string; id: number };
type Beam = { x: number; y: number; active: boolean };
type ScoreEntry = { name: string; score: number };

// ----- UTILITY FUNCTIONS -----
// Return a new obstacle for a consistent gap size and passage
function getRandomObstacle(id: number): Obstacle {
  const min = 10;
  const max = CONFIG.GAME_HEIGHT - CONFIG.OBSTACLE_GAP - 10;
  const height = Math.floor(Math.random() * (max - min) + min);
  return { x: CONFIG.GAME_WIDTH, height, id };
}

// Return a new random Dementor enemy (Y coordinate randomized)
function getRandomEnemy(id: number): Enemy {
  const y = Math.floor(Math.random() * (CONFIG.GAME_HEIGHT - CONFIG.ENEMY_H - 20)) + 10;
  // Only Dementors by default; can use random type/color if you want
  return { x: CONFIG.GAME_WIDTH, y, color: "#1a1a1a", type: "Dementor", id };
}

// ----- MAIN GAME COMPONENT -----
export default function App() {
  // --- Game State ---
  const [playerY, setPlayerY] = useState(CONFIG.GAME_HEIGHT / 2);
  const [vel, setVel] = useState(0);
  const [obstacles, setObstacles] = useState<Obstacle[]>([getRandomObstacle(0)]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [beams, setBeams] = useState<Beam[]>([]);
    const [score, setScore] = useState(0);

  const [running, setRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [name, setName] = useState("");
    const [leaderboard, setLeaderboard] = useState<ScoreEntry[]>([]);
   const [showIntro, setShowIntro] = useState(true);
   const [showHelp, setShowHelp] = useState(false);
   const [isPaused, setIsPaused] = useState(false);

  // For unique IDs (refs, so do not cause rerenders)
  const obstacleId = useRef(1);
  const enemyId = useRef(1);

   // ----- MAIN GAME LOOP (runs every 24ms) -----
   useEffect(() => {
     if (!running || isPaused) return;
    const interval = setInterval(() => {
      // Apply gravity to Harry's vertical velocity
      setVel(v => Math.min(v + CONFIG.GRAVITY, 10));
      // Move Harry vertically, keeping within vertical bounds
      setPlayerY(y => Math.max(0, Math.min(CONFIG.GAME_HEIGHT - CONFIG.PLAYER_H, y + vel)));
      setScore(s => s + 1);

      // Move pillars leftward, spawn new pillars when necessary
      setObstacles(obs => {
        let nobs = obs.map(o => ({ ...o, x: o.x - 3 }));
        if (!gameOver && (nobs.length === 0 || nobs[nobs.length - 1].x < CONFIG.GAME_WIDTH - 200)) {
          nobs.push(getRandomObstacle(obstacleId.current++));
        }
        return nobs.filter(o => o.x + CONFIG.OBSTACLE_W > 0);
      });

      // Move enemies leftward, rarely spawn new ones, trim offscreen
      setEnemies(enms => {
        let nenms = enms.map(e => ({ ...e, x: e.x - 5 }));
        if (Math.random() > 0.985) {
          nenms.push(getRandomEnemy(enemyId.current++));
        }
        return nenms.filter(e => e.x + CONFIG.ENEMY_W > 0);
      });

      // Move beams to the right, remove offscreen or inactive
      setBeams(bs =>
        bs.filter(b => b.active && b.x < CONFIG.GAME_WIDTH).map(b => ({ ...b, x: b.x + 16 }))
      );
    }, 24);
    return () => clearInterval(interval);
  }, [vel, running, gameOver]);

  // ----- INPUT HANDLING -----
    useEffect(() => {
            if (!running) return;

    // Make Harry flap
    const flap = () => setVel(CONFIG.FLAP_VEL);
    // Cast a magic beam
    const cast = () => setBeams(bs => [...bs, { x: 30 + CONFIG.PLAYER_W, y: playerY + CONFIG.PLAYER_H / 2 - 4, active: true }]);

     // Keyboard handler
     const onKey = (e: KeyboardEvent) => {
       // Prevent scrolling on arrows during game
       if (e.key === "ArrowLeft" || e.key === "ArrowRight" || e.key === "ArrowUp" || e.key === "ArrowDown") {
         e.preventDefault();
       }
       // P key: pause/unpause game
       if (e.key === "p" || e.key === "P") {
         e.preventDefault();
         setIsPaused(!isPaused);
         return;
       }
       // Space bar: cast magic beam
       if (e.code === "Space") { e.preventDefault(); cast(); }
       // Up arrow: flap
       if (e.key === "ArrowUp") flap();
     };

    window.addEventListener("keydown", onKey);
    window.addEventListener("mousedown", flap); // For mouse click flap
    window.addEventListener("touchstart", flap); // For tap flap

    return () => {
      window.removeEventListener("keydown", onKey);
      window.removeEventListener("mousedown", flap);
      window.removeEventListener("touchstart", flap);
    };
   }, [playerY, running, isPaused]);

  // ----- COLLISION DETECTION -----
    useEffect(() => {
        if (!running) return;
    // Harry's bounding box
    const harryTop = playerY, harryBot = playerY + CONFIG.PLAYER_H, harryLeft = 30, harryRight = 30 + CONFIG.PLAYER_W;

    // Pillar collision (with a forgiving margin of 3px)
    for (const obs of obstacles) {
      if (
        obs.x < harryRight + 3 &&
        obs.x + CONFIG.OBSTACLE_W > harryLeft - 3 &&
        (harryTop < obs.height - 3 || harryBot > obs.height + CONFIG.OBSTACLE_GAP + 3)
      ) {
        setRunning(false); setGameOver(true);
      }
    }

    // Enemy collision (forgiveness margin 2px)
    for (const enemy of enemies) {
      if (
        enemy.x < harryRight + 2 &&
        enemy.x + CONFIG.ENEMY_W > harryLeft - 2 &&
        enemy.y < harryBot + 2 &&
        enemy.y + CONFIG.ENEMY_H > harryTop - 2
      ) {
        setRunning(false); setGameOver(true);
      }
    }

    // Beams collide with enemies -- both are removed
    setBeams(bs => {
      const newBeams = [...bs];
      setEnemies(enms => enms.filter(enemy => {
        let hit = false;
        for (let i = 0; i < newBeams.length; i++) {
          const beam = newBeams[i];
          // Generous hitbox for fun
          if (beam.active && beam.y > enemy.y - 5 && beam.y < enemy.y + CONFIG.ENEMY_H + 5 && beam.x + CONFIG.BEAM_W > enemy.x - 5 && beam.x < enemy.x + CONFIG.ENEMY_W + 5) {
            hit = true; newBeams[i] = { ...beam, active: false }; break;
          }
        }
        return !hit;
      }));
      return newBeams;
    });

    // Game over if Harry hits top or bottom of screen
    if (playerY <= 0 || playerY + CONFIG.PLAYER_H >= CONFIG.GAME_HEIGHT) {
      setRunning(false); setGameOver(true);
    }
  }, [playerY, obstacles, enemies, beams, running]);

  // ----- LEADERBOARD -----
    useEffect(() => {
    fetch(`${CONFIG.API}/leaderboard`)
            .then(res => res.json())
      .then(setLeaderboard)
      .catch(() => setLeaderboard([]));
  }, [gameOver]);

  // ----- GAME FLOW HANDLERS -----
  function startGame() {
    setPlayerY(CONFIG.GAME_HEIGHT / 2);
    setVel(0);
    setObstacles([getRandomObstacle(0)]);
    setEnemies([]);
    setBeams([]);
        setScore(0);
    setGameOver(false);
        setRunning(true);
    setShowIntro(false);
    obstacleId.current = 1;
    enemyId.current = 1;
  }
   function submitScore() {
     // Validate name
     const trimmedName = name.trim();
     if (!trimmedName) {
       alert("Please enter a name before submitting your score!");
       return;
     }

     // Check for duplicate names
     const isDuplicate = leaderboard.some(entry => 
       entry.name.toLowerCase() === trimmedName.toLowerCase()
     );

     if (isDuplicate) {
       alert("This username is already taken! Please choose a different name.");
       return;
     }

     fetch(`${CONFIG.API}/submit`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
       body: JSON.stringify({ name: trimmedName, score }),
     })
       .then(() => fetch(`${CONFIG.API}/leaderboard`))
                .then(res => res.json())
       .then(setLeaderboard)
       .then(() => {
         // Return to main menu after successful submission
         setShowIntro(true);
         setGameOver(false);
         setName(""); // Clear the name field
       });
   }

  // ----- RENDER -----
    return (
    <div style={{ textAlign: "center", fontFamily: "serif", background: "#1c1746", minHeight: "100vh", color: "#fff" }}>
      {/* Game Title */}
      <h2 style={{ color: "#f3ce13", margin: 0, padding: "16px 0", fontFamily: "Harry Potter, serif" }}>Harry Potter: Broomstick Blitz!</h2>

       {/* Help Modal */}
       {showHelp && (
         <div style={{
           position: "fixed", left: 0, top: 0, width: "100%", height: "100%", zIndex: 100,
           background: "rgba(0,0,0,0.79)", display: "flex", alignItems: "center", justifyContent: "center"
         }}>
           <div style={{
             background: "#fafaf3", color: "#151515", borderRadius: "12px", padding: "44px 32px", minWidth: "330px", maxWidth: "90vw", lineHeight: 1.6, fontSize: 17
           }}>
             <h2 style={{ color: "#014674" }}>How to Play</h2>
             <ul>
               <li><b>Click / Tap / Up Arrow</b> - Flap Harry upward</li>
               <li><b>Space Bar</b> - Shoot a magic beam straight right</li>
             </ul>
             <p>
               <b>Goal:</b> Fly as far as you can! Avoid obstacles and defeat Dementors (black enemies) using your magic beam.<br />
               Score points by surviving longer and zapping more Dementors!
             </p>
             <button
               onClick={() => setShowHelp(false)}
               style={{
                 marginTop: "18px", padding: "10px 24px", background: "linear-gradient(45deg, #f3ce13, #ffd700)",
                 color: "#243", border: "none", borderRadius: "6px", fontWeight: "bold", cursor: "pointer", fontSize: "15px", boxShadow: "0 4px 8px rgba(0,0,0,0.2)"
               }}>Close</button>
           </div>
         </div>
       )}

       {/* Main Game and Leaderboard */}
       <div style={{ display: "flex", justifyContent: "center", alignItems: "flex-start" }}>
         {/* Game Area, Intro Screen, or Game Over Screen */}
         <div style={{ position: "relative" }}>
           {/* Pause Button - only show when game is running */}
           {running && !isPaused && (
             <button
               onClick={() => setIsPaused(true)}
               style={{
                 position: "absolute",
                 top: "10px",
                 left: "10px",
                 zIndex: 10,
                 background: "rgba(0,0,0,0.7)",
                 color: "#f3ce13",
                 border: "2px solid #f3ce13",
                 borderRadius: "5px",
                 padding: "8px 12px",
                 cursor: "pointer",
                 fontSize: "14px",
                 fontWeight: "bold"
               }}
             >
               ‚è∏Ô∏è Pause (P)
             </button>
           )}
           {showIntro ? (
             /* Intro Screen - takes over the game display */
             <div style={{
               width: CONFIG.GAME_WIDTH, height: CONFIG.GAME_HEIGHT, 
               background: "linear-gradient(135deg, #1a1a2e, #16213e)", 
               border: "4px solid #f3ce13", borderRadius: "8px",
               display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center",
               textAlign: "center", padding: "20px", boxShadow: "0 8px 25px rgba(0,0,0,0.4)"
             }}>
               <h2 style={{ color: "#f3ce13", margin: "0 0 20px 0", fontSize: "24px", textShadow: "2px 2px 4px rgba(0,0,0,0.5)" }}>
                 Welcome to Hogwarts!
               </h2>
               <p style={{ color: "#fff", margin: "0 0 30px 0", fontSize: "16px", lineHeight: "1.5" }}>
                 Fly through obstacles, defeat Dementors with magic beams, and achieve the highest score!
               </p>
               <div style={{ display: "flex", flexDirection: "column", gap: "12px", width: "100%" }}>
                 <button onClick={startGame} style={{
                   padding: "12px 24px", background: "linear-gradient(45deg, #4CAF50, #45a049)",
                   color: "#fff", border: "none", borderRadius: "8px", fontWeight: "bold", cursor: "pointer",
                   fontSize: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
                 }}>
                   Start Game
                 </button>
                 <button onClick={() => setShowHelp(true)} style={{
                   padding: "12px 24px", background: "linear-gradient(45deg, #0097a7, #00bcd4)",
                   color: "#fff", border: "none", borderRadius: "8px", fontWeight: "bold", cursor: "pointer",
                   fontSize: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
                 }}>
                   Get Help
                 </button>
               </div>
             </div>
           ) : !running && gameOver ? (
             /* Game Over Screen - replaces the game display */
             <div style={{
               width: CONFIG.GAME_WIDTH, height: CONFIG.GAME_HEIGHT, 
               background: "linear-gradient(135deg, #1a1a2e, #16213e)", 
               border: "4px solid #f3ce13", borderRadius: "8px",
               display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center",
               textAlign: "center", padding: "20px", boxShadow: "0 8px 25px rgba(0,0,0,0.4)"
             }}>
               <h2 style={{ color: "#ff6b6b", margin: "0 0 20px 0", fontSize: "28px", textShadow: "2px 2px 4px rgba(0,0,0,0.5)" }}>
                 Game Over!
               </h2>
               <div style={{ fontSize: "20px", color: "#f3ce13", marginBottom: "25px", fontWeight: "bold" }}>
                 Final Score: {score}
               </div>
               <div style={{ marginBottom: "20px" }}>
                 <input
                   style={{
                     padding: "10px 14px", borderRadius: "6px", border: "2px solid #f3ce13",
                     background: "#2d2d2d", color: "#fff", fontSize: "14px", width: "200px", textAlign: "center"
                   }}
                   placeholder="Enter your name"
                   value={name}
                   onChange={e => setName(e.target.value)}
                 />
               </div>
               <div style={{ display: "flex", flexDirection: "column", gap: "10px", width: "100%" }}>
                 <button onClick={submitScore}
                   style={{
                     padding: "10px 20px", background: "linear-gradient(45deg, #f3ce13, #ffd700)",
                     color: "#000", border: "none", borderRadius: "6px", fontWeight: "bold", cursor: "pointer",
                     fontSize: "14px", boxShadow: "0 4px 8px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
                   }}>
                   Submit Score
                 </button>
                 <button onClick={startGame}
                   style={{
                     padding: "10px 20px", background: "linear-gradient(45deg, #2196F3, #1976D2)",
                     color: "#fff", border: "none", borderRadius: "6px", fontWeight: "bold", cursor: "pointer",
                     fontSize: "14px", boxShadow: "0 4px 8px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
                   }}>
                   Play Again
                 </button>
                 <button onClick={() => { setShowIntro(true); setGameOver(false); }}
                   style={{
                     padding: "10px 20px", background: "linear-gradient(45deg, #4CAF50, #45a049)",
                     color: "#fff", border: "none", borderRadius: "6px", fontWeight: "bold", cursor: "pointer",
                     fontSize: "14px", boxShadow: "0 4px 8px rgba(0,0,0,0.3)", textTransform: "uppercase", letterSpacing: "1px"
                   }}>
                   Main Menu
                 </button>
               </div>
             </div>
           ) : (
             /* Normal Game Display */
             <>
               <svg width={CONFIG.GAME_WIDTH} height={CONFIG.GAME_HEIGHT} style={{ background: "#222d45", border: "4px solid #665f51", borderRadius: "8px" }}>
                 {/* Stars */}
                 {[...Array(24)].map((_, i) => (
                   <circle key={i} cx={Math.random()*CONFIG.GAME_WIDTH} cy={Math.random()*CONFIG.GAME_HEIGHT} r={1.2} fill="white" opacity="0.5"/>
                 ))}
                 {/* Obstacles, Enemies, Beams, Harry */}
                 {obstacles.map(o => renderObstacle(o, CONFIG.OBSTACLE_W, CONFIG.OBSTACLE_GAP, CONFIG.GAME_HEIGHT))}
                 {enemies.map(e => renderEnemy(e, CONFIG.ENEMY_W, CONFIG.ENEMY_H))}
                 {renderBeams(beams)}
                 {renderHarry(playerY, beams, CONFIG.PLAYER_W, CONFIG.PLAYER_H)}
               </svg>
               <div style={{ 
                 fontSize: "24px", margin: "16px 0", color: "#f3ce13", 
                 fontWeight: "bold", textShadow: "2px 2px 4px rgba(0,0,0,0.5)" 
               }}>
                 Score: {score}
               </div>
             </>
           )}

           {/* Pause Overlay */}
           {isPaused && (
             <div style={{
               position: "absolute",
               top: 0,
               left: 0,
               width: CONFIG.GAME_WIDTH,
               height: CONFIG.GAME_HEIGHT,
               background: "rgba(0,0,0,0.8)",
               display: "flex",
               flexDirection: "column",
               alignItems: "center",
               justifyContent: "center",
               borderRadius: "8px",
               zIndex: 20
             }}>
               <h2 style={{ color: "#f3ce13", margin: "0 0 30px 0", fontSize: "28px" }}>
                 Game Paused
               </h2>
               <div style={{ display: "flex", flexDirection: "column", gap: "15px", width: "200px" }}>
                 <button
                   onClick={() => setIsPaused(false)}
                   style={{
                     padding: "12px 24px",
                     background: "linear-gradient(45deg, #4CAF50, #45a049)",
                     color: "#fff",
                     border: "none",
                     borderRadius: "8px",
                     fontWeight: "bold",
                     cursor: "pointer",
                     fontSize: "16px",
                     boxShadow: "0 4px 12px rgba(0,0,0,0.3)"
                   }}
                 >
                   Resume Game
                 </button>
                 <button
                   onClick={() => {
                     setIsPaused(false);
                     startGame();
                   }}
                   style={{
                     padding: "12px 24px",
                     background: "linear-gradient(45deg, #2196F3, #1976D2)",
                     color: "#fff",
                     border: "none",
                     borderRadius: "8px",
                     fontWeight: "bold",
                     cursor: "pointer",
                     fontSize: "16px",
                     boxShadow: "0 4px 12px rgba(0,0,0,0.3)"
                   }}
                 >
                   Restart Game
                 </button>
                 <button
                   onClick={() => setShowHelp(true)}
                   style={{
                     padding: "12px 24px",
                     background: "linear-gradient(45deg, #0097a7, #00bcd4)",
                     color: "#fff",
                     border: "none",
                     borderRadius: "8px",
                     fontWeight: "bold",
                     cursor: "pointer",
                     fontSize: "16px",
                     boxShadow: "0 4px 12px rgba(0,0,0,0.3)"
                   }}
                 >
                   Get Help
                 </button>
               </div>
             </div>
           )}
         </div>

         {/* Enhanced Leaderboard - 20px to the right of game display */}
         <div style={{
           minWidth: "280px", background: "linear-gradient(135deg, rgba(0,0,0,0.8), rgba(20,20,40,0.9))", 
           padding: "25px", borderRadius: "20px", border: "2px solid #f3ce13",
           boxShadow: "0 8px 25px rgba(0,0,0,0.4)", backdropFilter: "blur(10px)",
           marginLeft: "20px"
         }}>
           <div style={{ 
             fontSize: "24px", color: "#f3ce13", marginBottom: "20px", textAlign: "center",
             textShadow: "2px 2px 4px rgba(0,0,0,0.5)", fontWeight: "bold"
           }}>
             üèÜ Leaderboard
           </div>
           {leaderboard.length === 0 ? (
             <div style={{ 
               color: "#ccc", textAlign: "center", fontStyle: "italic", 
               padding: "30px 0", fontSize: "16px"
             }}>
               No scores yet
             </div>
           ) : (
             <div style={{ maxHeight: "400px", overflowY: "auto" }}>
               {leaderboard.slice(0, 10).map((entry, i) => (
                 <div key={i} style={{
                   display: "flex", justifyContent: "space-between", alignItems: "center",
                   padding: "12px 16px", margin: "8px 0", borderRadius: "10px",
                   background: i < 3 ? "rgba(243, 206, 19, 0.15)" : "rgba(255,255,255,0.05)",
                   border: i < 3 ? "1px solid rgba(243, 206, 19, 0.4)" : "1px solid transparent",
                   transition: "all 0.3s ease"
                 }}>
                   <div style={{
                     display: "flex", alignItems: "center", gap: "8px"
                   }}>
                     <span style={{
                       fontSize: "18px", fontWeight: "bold",
                       color: i === 0 ? "#ffd700" : i === 1 ? "#c0c0c0" : i === 2 ? "#cd7f32" : "#fff"
                     }}>
                       {i === 0 ? "ü•á" : i === 1 ? "ü•à" : i === 2 ? "ü•â" : `${i + 1}.`}
                     </span>
                     <span style={{
                       color: i === 0 ? "#ffd700" : i === 1 ? "#c0c0c0" : i === 2 ? "#cd7f32" : "#fff",
                       fontWeight: i < 3 ? "bold" : "normal", fontSize: i < 3 ? "16px" : "15px"
                     }}>
                       {entry.name}
                     </span>
                   </div>
                   <span style={{
                     color: "#f3ce13", fontWeight: "bold", fontSize: i < 3 ? "16px" : "15px",
                     textShadow: "1px 1px 2px rgba(0,0,0,0.5)"
                   }}>
                     {entry.score.toLocaleString()}
                   </span>
            </div>
               ))}
                </div>
            )}
         </div>
       </div>
        </div>
    );
}
